INSERT INTO `judul_surah` (`id`, `nama`, `nama_arab`, `arti_surah`, `deskripsi_surah`, `id_surah`) VALUES
(1, 'An-Nas', 'الناس', 'Manusia', 'Surat ini terdiri atas 6 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Falaq. Nama An Naas diambil dari An Naas yang berulang kali disebut dalam surat ini yang artinya manusia.', 114),
(2, 'Al-Falaq', 'الفلق', 'Subuh', 'Surat ini terdiri atas 5 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Fiil. Nama Al Falaq diambil dari kata Al Falaq yang terdapat pada ayat pertama surat ini yang artinya waktu subuh. Diriwayatkan oleh Abu Daud, At Tirmizi dan An Nasa-i dari \'Uqbah bin \'Aamir bahwa Rasulullah s.a.w. bersembahyan dengan membaca surat Al Falaq dan surat An Naas dalam perjalanan.', 113),
(3, 'Al-Ikhlas', 'الاخلاص', 'Ikhlas', 'Surat ini terdiri atas 4 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah sesudah surat An Naas. Dinamakan Al Ikhlas karena surat ini sepenuhnya menegaskan kemurnian keesaan Allah s.w.t.', 112),
(4, 'Al-Lahab', 'اللهب', 'Api Yang Bergejolak', 'Surat ini terdiri atas 5 ayat, termasuk golongan surat-surat Makkiyyah,  diturunkan sesudah surat Al Fath. Nama Al Lahab diambil dari kata  Al Lahab yang terdapat pada ayat ketiga surat ini yang artinya gejolak  api. Surat ini juga dinamakan surat Al Masad.', 111),
(5, 'An-Nasr', 'النصر', 'Pertolongan', 'Surat An Nashr terdiri atas 3 ayat, termasuk golongan surat-surat  Madaniyyah yang diturunkan di Mekah sesudah surat At Taubah.  Dinamai An Nashr (pertolongan) diambil dari perkataan Nashr yang  terdapat pada ayat pertama surat ini.', 110),
(6, 'Al-Kafirun', 'الكٰفرون', 'Orang-Orang Kafir', 'Surat Al Kaafiruun terdiri atas 6 ayat, termasuk golongan surat-surat  Makkiyyah, diturunkan sesudah surat Al Maa\'uun. Dinamai Al Kaafiruun (orang-orang kafir), diambil dari perkataan  Al Kaafiruun yang terdapat pada ayat pertama surat ini.', 109),
(7, 'Al-Kautsar', 'الكوثر', 'Pemberian Yang Banyak', 'Surat Al Kautsar terdiri atas 3 ayat, termasuk golongan surat-surat  Makkiyyah diturunkan sesudah surat Al \'Aadiyaat. Dinamai Al Kautsar (nikmat yang banyak) diambil dari perkataan Al Kautsar yang terdapat pada ayat pertama surat ini.Surat ini sebagai penghibur hati Nabi Muhammad s.a.w.', 108),
(8, 'Al-Maun', 'الماعون', 'Barang Yang Berguna', 'Surat ini terdiri atas 7 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat At Taakatsur. Nama Al Maa\'uun diambil dari kata Al Maa\'uun yang terdapat pada ayat 7, artinya barang-barang yang berguna.', 107),
(9, 'Al-Quraisy', 'القريش', 'Quraisy', 'Surat ini terdiri atas 4 ayat, termasuk golongan surat-surat Makkiyyah dan diturunkan sesudah surat At Tiin. Nama Quraisy diambil dari kata Quraisy yang terdapat pada ayat pertama, artinya suku Quraisy. Suku Quraisy adalah suku yang mendapat  kehormatan untuk memelihara Ka\'bah.', 106),
(10, 'Al-Fil', 'الفيل', 'Gajah', 'Surat ini terdiri atas 5 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Kaafirun. Nama Al Fiil diambil dari kata Al Fiil yang terdapat pada ayat pertama surat ini, artinya gajah. Surat Al Fiil mengemukakan cerita pasukan bergajah dari Yaman yang dipimpin oleh Abrahah yang ingin meruntuhkan Ka\'bah di Mekah. Peristiwa ini terjadi pada tahun Nabi Muhammad s.a.w. dilahirkan.', 105),
(11, 'Al-Humazah', 'الهمزة', 'Pengumpat', 'Surat Al Humazah terdiri atas 9 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Qiyaamah. Dinamai Al Humazah (pengumpat) diambil dari perkataan Humazah yang terdapat pada ayat pertama surat ini.', 104),
(12, 'Al-Asr', 'العصر', 'Asar', 'Surat Al \'Ashr terdiri atas 3 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Alam Nasyrah. Dinamai Al \'Ashr (masa) diambil dari perkataan Al \'Ashr yang terdapat pada ayat pertama surat ini.', 103),
(13, 'At-Takatsur', 'التكاثر', 'Bermegah-Megahan', 'Surat At Takaatsur terdiri atas 8 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Kautsar. Dinamai At Takaatsur (bermegah-megahan) diambil dari perkataan At Takaatsur yang terdapat pada ayat pertama surat ini.', 102),
(14, 'Al-Qoriah', 'القارعة', 'Hari Kiamat', 'Surat ini terdiri atas 11 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Quraisy. Nama Al Qaari\'ah diambil dari kata Al Qaari\'ah yang terdapat pada ayat pertama, artinya mengetok dengan keras, kemudian kata ini dipakai untuk nama hari kiamat.', 101),
(15, 'Al-Adiyat', 'العٰديٰت', 'Kuda Yang Berlari Kencang', 'Surat ini terdiri atas 11 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al\'Ashr. Nama Al \'Aadiyaat diambil dari kata Al \'Aadiyaat yang terdapat pada ayat pertama surat ini, artinya yang berlari kencang.', 100),
(16, 'Al-Zalzalah', 'الزلزلة', 'Guncangan', 'Surat ini terdiri atas 8 ayat, termasuk golongan surat-surat Madaniyyah diturunkan sesudah surat An Nisaa\'. Nama Al Zalzalah diambil dari kata: Zilzaal yang terdapat pada ayat pertama surat ini yang berarti goncangan.', 99),
(17, 'Al-Bayyinah', 'البيّنة', 'Bukti Nyata', 'Surat Al Bayyinah terdiri atas 8 ayat, termasuk golongan surat-surat Madaniyyah, diturunkan sesudah surat Ath Thalaq. Dinamai Al Bayyinah (bukti yang nyata) diambil dari perkataan Al Bayyinah yang terdapat pada ayat pertama surat ini.', 98),
(18, 'Al-Qadr', 'القدر', 'Kemuliaan', 'Surat Al Qadr terdiri atas 5 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat \'Abasa. Surat ini dinamai Al Qadr (kemuliaan), diambil dari perkataan Al Qadr yang terdapat pada ayat pertama surat ini.', 97),
(19, 'Al-Alaq', 'العلق', 'Segumpal Darah', 'Surat Al \'Alaq terdiri atas 19 ayat, termasuk golongan surat-surat Makkiyah. Ayat 1 sampai dengan 5 dari surat ini adalah ayat-ayat Al Quran yang pertama sekali diturunkan, yaitu di waktu Nabi Muhammad s.a.w. berkhalwat di gua Hira\'. Surat ini dinamai Al \'Alaq (segumpal darah), diambil dari perkataan Alaq yang terdapat pada ayat 2 surat ini. Surat ini dinamai juga dengan Iqra atau Al Qalam.', 96),
(20, 'At-Tin', 'التين', 'Buah Tin', 'Surat ini terdiri atas 8 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Buruuj. Nama At Tiin diambil dari kata At Tiin yang terdapat pada ayat pertama surat ini yang artinya buah Tin.', 95),
(21, 'Asy-Syarh', 'الشرح', 'Lapang', 'Surat ini terdiri atas 8 ayat, termasuk golongan surat-surat Makkiyah dan diturunkan sesudah surat Adh Dhuhaa. Nama Alam Nasyrah diambil dari kata Alam Nasyrah yang terdapat pada ayat pertama, yang berarti: bukankah Kami telah melapangkan.', 94),
(22, 'Ad-Duha', 'الضحى', 'Duha', 'Surat ini terdiri atas 11 ayat, termasuk golongan surat Makiyyah dan diturunkan sesudah surat Al Fajr. Nama Adh Dhuhaa diambil dari kata yang terdapat pada ayat pertama, artinya : waktu matahari sepenggalahan naik.', 93),
(23, 'Al-Lail', 'الّيل', 'Malam', 'Surat ini terdiri atas 21 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al A\'laa. Surat ini dinamai Al Lail (malam), diambil dari perkataan Al Lail yang terdapat pada ayat pertama surat ini', 92),
(24, 'Asy-Syams', 'الشمس', 'Matahari', 'Surat Asy Syams terdiri atas 15 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Qadar.  Dinamai Asy Syams (matahari) diambil dari perkataan Asy Syams yang terdapat pada ayat permulaan surat ini.', 91),
(25, 'Al-Balad', 'البلد', 'Negeri', 'Surat Al Balad terdiri atas 20 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Qaaf. Dinamai Al Balad, diambil dari perkataan Al Balad yang terdapat  pada ayat pertama surat ini. Yang dimaksud dengan kota di sini ialah kota Mekah.', 90),
(26, 'Al-Fajr', 'الفجر', 'Fajar', 'Surat ini terdiri atas 30 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Lail. Nama Al Fajr diambil dari kata Al Fajr yang terdapat pada ayat pertama surat ini yang artinya fajar.', 89),
(27, 'Al-Ghasyiyah', 'الغاشية', 'Hari Kiamat', 'Surat ini terdiri atas 26 ayat, termasuk surat-surat Makkiyah, diturunkan sesudah surat Adz Dzaariat. Nama Ghaasyiyah diambil dari kata Al Ghaasyiyah yang terdapat pada ayat pertama surat ini yang  artinya peristiwa yang dahsyat, tapi yang dimaksud adalah hari kiamat. Surat ini adalah surat yang kerap kali dibaca Nabi pada rakaat kedua  pada shalat hari-hari Raya dan shalat Jum\'at', 88),
(28, 'Al-A\'la', 'الاعلى', 'Maha Tinggi', 'Surat ini terdiri atas 19 ayat, termasuk golongan surat-surat Makkiyyah, dan diturunkan sesudah surat At Takwiir. Nama Al A´laa diambil dari kata Al A´laa yang terdapat pada ayat pertama, berarti Yang Paling Tinggi. Muslim meriwayatkan dalam kitab Al Jumu\'ah, dan diriwayatkan pula oleh Ashhaabus Sunan, dari Nu\'man ibnu Basyir bahwa Rasulullah s.a.w. pada shalat dua hari Raya (Fitri dan Adha) dan shalat Jum\'at membaca surat Al A´laa pada rakaat pertama, dan surat Al Ghaasyiyah pada rakaat kedua.', 87),
(29, 'Ath-Thariq', 'الطارق', 'Yang Datang Di Malam Hari', 'Surat Ath Thaariq terdiri atas 17 ayat, termasuk golongan surat-surat Makkiyah,  diturunkan sesudah surat Al Balad.  Dinamai Ath Thaariq (yang datang di malam hari) diambil dari  perkataan Ath Thaariq yang terdapat pada ayat 1 surat ini.', 86),
(30, 'Al-Buruj', 'البروج', 'Gugusan Bintang', 'Surat Al Buruuj terdiri atas 22 ayat, termasuk golongan surat-surat Makkiyyah diturunkan sesudah surat Asy-Syams.Dinamai Al Buruuj (gugusan bintang) diambil dari perkataan Al Buruuj yang terdapat pada ayat 1 surat ini.', 85),
(31, 'Al-Insyiqaq', 'الانشقاق', 'Terbelah', 'Surat Al Insyiqaaq, terdiri atas 25 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Infithaarr. Dinamai Al Insyiqaaq (terbelah), diambil dari perkataan Insyaqqat yang terdapat pada permulaan surat ini, yang pokok katanya ialah insyiqaaq.', 84),
(32, 'Al-Mutaffifin', 'المطفّفين', 'Orang Orang Curang', 'Surat ini terdiri atas 36 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al \'Ankabuut dan merupakan  surat yang terakhir di Mekkah sebelum hijrah. Al Muthaffifiin  yang dijadikan nama bagi surat ini diambil dari kata  Al Muthaffifiin yang terdapat pada ayat pertama.', 83),
(33, 'Al-Infitar', 'الانفطار', 'Terbelah', 'Surat ini terdiri atas 19 ayat, termasuk golongan surat-surat Makkiyah dan diturunkan sesudah surat An Naazi\'aat. Al Infithaar yang dijadikan  nama untuk surat ini adalah kata asal dari kata Infatharat (terbelah)  yang terdapat pada ayat pertama.', 82),
(34, 'At-Takwir', 'التكوير', 'Penggulungan', 'Surat At Takwir terdiri atas 29 ayat dan termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Masadd. Kata At Takwir (terbelah) yang menjadi nama bagi surat ini adalah dari kata asal (mashdar) dari kata kerja kuwwirat (digulung) yang terdapat pada ayat pertama surat ini.', 81),
(35, '\'Abasa', 'عبس', 'Bermuka Masam', 'Surat \'Abasa terdiri atas 42 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat An Najm. Dinamai \'Abasa  diambil dari perkataan \'Abasa yang terdapat pada ayat pertama surat ini. Menurut riwayat, pada suatu ketika Rasulullah s.a.w. menerima dan berbicara dengan pemuka-pemuka Quraisy yang beliau harapkan agar mereka masuk Islam. Dalam pada itu datanglah Ibnu Ummi Maktum, seorang sahabat yang buta yang mengharap agar Rasulullah s.a.w. membacakan kepadanya ayat- ayat Al Quran yang telah diturunkan Allah. tetapi Rasulullah s.a.w. bermuka masam dan memalingkan muka dari Ibnu Ummi Maktum yang buta itu, lalu Allah menurunkan surat ini sebagai teguran atas sikap Rasulullah terhadap ibnu Ummi Maktum itu.', 80),
(36, 'An-Nazi\'at', 'النّٰزعٰت', 'Malaikat Yang Mencabut', 'Surat An Naazi´aat terdiri atas 46 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat An Naba´. Dinamai An Naazi´aat diambil dari perkataan An Naazi´aat yang terdapat pada ayat pertama surat ini. Dinamai pula as Saahirah yang diambil dari ayat 14, dinamai juga Ath Thaammah diambil dari ayat 34.', 79),
(37, 'An-Naba\'', 'النبأ', 'Berita Besar', 'Surat An Naba´ terdiri atas 40 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Ma´aarij. Dinamai An Naba´ (berita besar) diambil dari perkataan An Naba´ yang terdapat pada ayat 2 surat ini. Dinamai juga Amma yatasaa aluun diambil dari perkataan Amma yatasaa aluun yang terdapat pada ayat 1 surat ini.', 78);
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `indoText`, `readText`) VALUES
(5672, 78, 1, 'عَمَّ يَتَسَاۤءَلُوْنَۚ', 'Tentang apakah mereka saling bertanya-tanya?', '\'amma yatasā`alụn'),
(5673, 78, 2, 'عَنِ النَّبَاِ الْعَظِيْمِۙ', 'Tentang berita yang besar (hari kebangkitan),', '\'anin-naba`il-\'aẓīm'),
(5674, 78, 3, 'الَّذِيْ هُمْ فِيْهِ مُخْتَلِفُوْنَۗ', 'yang dalam hal itu mereka berselisih.', 'allażī hum fīhi mukhtalifụn'),
(5675, 78, 4, 'كَلَّا سَيَعْلَمُوْنَۙ', 'Tidak! Kelak mereka akan mengetahui,', 'kallā saya\'lamụn'),
(5676, 78, 5, 'ثُمَّ كَلَّا سَيَعْلَمُوْنَ', 'sekali lagi tidak! Kelak mereka akan mengetahui.', 'ṡumma kallā saya\'lamụn'),
(5677, 78, 6, 'اَلَمْ نَجْعَلِ الْاَرْضَ مِهٰدًاۙ', 'Bukankah Kami telah menjadikan bumi sebagai hamparan,', 'a lam naj\'alil-arḍa mihādā'),
(5678, 78, 7, 'وَّالْجِبَالَ اَوْتَادًاۖ', 'dan gunung-gunung sebagai pasak?', 'wal-jibāla autādā'),
(5679, 78, 8, 'وَّخَلَقْنٰكُمْ اَزْوَاجًاۙ', 'Dan Kami menciptakan kamu berpasang-pasangan,', 'wa khalaqnākum azwājā'),
(5680, 78, 9, 'وَّجَعَلْنَا نَوْمَكُمْ سُبَاتًاۙ', 'dan Kami menjadikan tidurmu untuk istirahat,', 'wa ja\'alnā naumakum subātā'),
(5681, 78, 10, 'وَّجَعَلْنَا الَّيْلَ لِبَاسًاۙ', 'dan Kami menjadikan malam sebagai pakaian,', 'wa ja\'alnal-laila libāsā'),
(5682, 78, 11, 'وَّجَعَلْنَا النَّهَارَ مَعَاشًاۚ', 'dan Kami menjadikan siang untuk mencari penghidupan,', 'wa ja\'alnan-nahāra ma\'āsyā'),
(5683, 78, 12, 'وَبَنَيْنَا فَوْقَكُمْ سَبْعًا شِدَادًاۙ', 'dan Kami membangun di atas kamu tujuh (langit) yang kokoh,', 'wa banainā fauqakum sab\'an syidādā'),
(5684, 78, 13, 'وَّجَعَلْنَا سِرَاجًا وَّهَّاجًاۖ', 'dan Kami menjadikan pelita yang terang-benderang (matahari),', 'wa ja\'alnā sirājaw wahhājā'),
(5685, 78, 14, 'وَّاَنْزَلْنَا مِنَ الْمُعْصِرٰتِ مَاۤءً ثَجَّاجًاۙ', 'dan Kami turunkan dari awan, air hujan yang tercurah dengan hebatnya,', 'wa anzalnā minal-mu\'ṣirāti mā`an ṡajjājā'),
(5686, 78, 15, 'لِّنُخْرِجَ بِهٖ حَبًّا وَّنَبَاتًاۙ', 'untuk Kami tumbuhkan dengan air itu biji-bijian dan tanam-tanaman,', 'linukhrija bihī ḥabbaw wa nabātā'),
(5687, 78, 16, 'وَّجَنّٰتٍ اَلْفَافًاۗ', 'dan kebun-kebun yang rindang.', 'wa jannātin alfāfā'),
(5688, 78, 17, 'اِنَّ يَوْمَ الْفَصْلِ كَانَ مِيْقَاتًاۙ', 'Sungguh, hari keputusan adalah suatu waktu yang telah ditetapkan,', 'inna yaumal-faṣli kāna mīqātā'),
(5689, 78, 18, 'يَّوْمَ يُنْفَخُ فِى الصُّوْرِ فَتَأْتُوْنَ اَفْوَاجًاۙ', '(yaitu) pada hari (ketika) sangkakala ditiup, lalu kamu datang berbondong-bondong,', 'yauma yunfakhu fiṣ-ṣụri fa ta`tụna afwājā'),
(5690, 78, 19, 'وَّفُتِحَتِ السَّمَاۤءُ فَكَانَتْ اَبْوَابًاۙ', 'dan langit pun dibukalah, maka terdapatlah beberapa pintu,', 'wa futiḥatis-samā`u fa kānat abwābā'),
(5691, 78, 20, 'وَّسُيِّرَتِ الْجِبَالُ فَكَانَتْ سَرَابًاۗ', 'dan gunung-gunung pun dijalankan sehingga menjadi fatamorgana.', 'wa suyyiratil-jibālu fa kānat sarābā'),
(5692, 78, 21, 'اِنَّ جَهَنَّمَ كَانَتْ مِرْصَادًاۙ', 'Sungguh, (neraka) Jahanam itu (sebagai) tempat mengintai (bagi penjaga yang mengawasi isi neraka),', 'inna jahannama kānat mirṣādā'),
(5693, 78, 22, 'لِّلطّٰغِيْنَ مَاٰبًاۙ', 'menjadi tempat kembali bagi orang-orang yang melampaui batas.', 'liṭ-ṭāgīna ma`ābā'),
(5694, 78, 23, 'لّٰبِثِيْنَ فِيْهَآ اَحْقَابًاۚ', 'Mereka tinggal di sana dalam masa yang lama,', 'lābiṡīna fīhā aḥqābā'),
(5695, 78, 24, 'لَا يَذُوْقُوْنَ فِيْهَا بَرْدًا وَّلَا شَرَابًاۙ', 'mereka tidak merasakan kesejukan di dalamnya dan tidak (pula mendapat) minuman,', 'lā yażụqụna fīhā bardaw wa lā syarābā'),
(5696, 78, 25, 'اِلَّا حَمِيْمًا وَّغَسَّاقًاۙ', 'selain air yang mendidih dan nanah,', 'illā ḥamīmaw wa gassāqā'),
(5697, 78, 26, 'جَزَاۤءً وِّفَاقًاۗ', 'sebagai pembalasan yang setimpal.', 'jazā`aw wifāqā'),
(5698, 78, 27, 'اِنَّهُمْ كَانُوْا لَا يَرْجُوْنَ حِسَابًاۙ', 'Sesungguhnya dahulu mereka tidak pernah mengharapkan perhitungan.', 'innahum kānụ lā yarjụna ḥisābā'),
(5699, 78, 28, 'وَّكَذَّبُوْا بِاٰيٰتِنَا كِذَّابًاۗ', 'Dan mereka benar-benar mendustakan ayat-ayat Kami.', 'wa każżabụ bi`āyātinā kiżżābā'),
(5700, 78, 29, 'وَكُلَّ شَيْءٍ اَحْصَيْنٰهُ كِتٰبًاۙ', 'Dan segala sesuatu telah Kami catat dalam suatu Kitab (buku catatan amalan manusia).', 'wa kulla syai`in aḥṣaināhu kitābā'),
(5701, 78, 30, 'فَذُوْقُوْا فَلَنْ نَّزِيْدَكُمْ اِلَّا عَذَابًا', 'Maka karena itu rasakanlah! Maka tidak ada yang akan Kami tambahkan kepadamu selain azab.', 'fa żụqụ fa lan nazīdakum illā \'ażābā'),
(5702, 78, 31, 'اِنَّ لِلْمُتَّقِيْنَ مَفَازًاۙ', 'Sungguh, orang-orang yang bertakwa mendapat kemenangan,', 'inna lil-muttaqīna mafāzā'),
(5703, 78, 32, 'حَدَاۤىِٕقَ وَاَعْنَابًاۙ', '(yaitu) kebun-kebun dan buah anggur,', 'ḥadā`iqa wa a\'nābā'),
(5704, 78, 33, 'وَّكَوَاعِبَ اَتْرَابًاۙ', 'dan gadis-gadis montok yang sebaya,', 'wa kawā\'iba atrābā'),
(5705, 78, 34, 'وَّكَأْسًا دِهَاقًاۗ', 'dan gelas-gelas yang penuh (berisi minuman).', 'wa ka`san dihāqā'),
(5706, 78, 35, 'لَا يَسْمَعُوْنَ فِيْهَا لَغْوًا وَّلَا كِذَّابًا', 'Di sana mereka tidak mendengar percakapan yang sia-sia maupun (perkataan) dusta.', 'lā yasma\'ụna fīhā lagwaw wa lā kiżżābā'),
(5707, 78, 36, 'جَزَاۤءً مِّنْ رَّبِّكَ عَطَاۤءً حِسَابًاۙ', 'Sebagai balasan dan pemberian yang cukup banyak dari Tuhanmu,', 'jazā`am mir rabbika \'aṭā`an ḥisābā'),
(5708, 78, 37, 'رَّبِّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا الرَّحْمٰنِ لَا يَمْلِكُوْنَ مِنْهُ خِطَابًاۚ', 'Tuhan (yang memelihara) langit dan bumi dan apa yang ada di antara keduanya; Yang Maha Pengasih, mereka tidak mampu berbicara dengan Dia.', 'rabbis-samāwāti wal-arḍi wa mā bainahumar-raḥmāni lā yamlikụna min-hu khiṭābā'),
(5709, 78, 38, 'يَوْمَ يَقُوْمُ الرُّوْحُ وَالْمَلٰۤىِٕكَةُ صَفًّاۙ لَّا يَتَكَلَّمُوْنَ اِلَّا مَنْ اَذِنَ لَهُ الرَّحْمٰنُ وَقَالَ صَوَابًا', 'Pada hari, ketika ruh dan para malaikat berdiri bersaf-saf, mereka tidak berkata-kata, kecuali siapa yang telah diberi izin kepadanya oleh Tuhan Yang Maha Pengasih dan dia hanya mengatakan yang benar.', 'yauma yaqụmur-rụḥu wal-malā`ikatu ṣaffal lā yatakallamụna illā man ażina lahur-raḥmānu wa qāla ṣawābā'),
(5710, 78, 39, 'ذٰلِكَ الْيَوْمُ الْحَقُّۚ فَمَنْ شَاۤءَ اتَّخَذَ اِلٰى رَبِّهٖ مَاٰبًا', 'Itulah hari yang pasti terjadi. Maka barang siapa menghendaki, niscaya dia menempuh jalan kembali kepada Tuhannya.', 'żālikal-yaumul-ḥaqq, fa man syā`attakhaża ilā rabbihī ma`ābā'),
(5711, 78, 40, 'اِنَّآ اَنْذَرْنٰكُمْ عَذَابًا قَرِيْبًا ەۙ يَّوْمَ يَنْظُرُ الْمَرْءُ مَا قَدَّمَتْ يَدَاهُ وَيَقُوْلُ الْكٰفِرُ يٰلَيْتَنِيْ كُنْتُ تُرَابًا', 'Sesungguhnya Kami telah memperingatkan kepadamu (orang kafir) azab yang dekat, pada hari manusia melihat apa yang telah diperbuat oleh kedua tangannya; dan orang kafir berkata, “Alangkah baiknya seandainya dahulu aku jadi tanah.”', 'innā anżarnākum \'ażābang qarībay yauma yanẓurul-mar`u mā qaddamat yadāhu wa yaqụlul-kāfiru yā laitanī kuntu turābā'),
(5712, 79, 1, 'وَالنّٰزِعٰتِ غَرْقًاۙ', 'Demi (malaikat) yang mencabut (nyawa) dengan keras.', 'wan-nāzi\'āti garqā'),
(5713, 79, 2, 'وَّالنّٰشِطٰتِ نَشْطًاۙ', 'Demi (malaikat) yang mencabut (nyawa) dengan lemah lembut.', 'wan-nāsyiṭāti nasyṭā'),
(5714, 79, 3, 'وَّالسّٰبِحٰتِ سَبْحًاۙ', 'Demi (malaikat) yang turun dari langit dengan cepat,', 'was-sābiḥāti sab-ḥā'),
(5715, 79, 4, 'فَالسّٰبِقٰتِ سَبْقًاۙ', 'dan (malaikat) yang mendahului dengan kencang,', 'fas-sābiqāti sabqā'),
(5716, 79, 5, 'فَالْمُدَبِّرٰتِ اَمْرًاۘ', 'dan (malaikat) yang mengatur urusan (dunia).', 'fal-mudabbirāti amrā'),
(5717, 79, 6, 'يَوْمَ تَرْجُفُ الرَّاجِفَةُۙ', '(Sungguh, kamu akan dibangkitkan) pada hari ketika tiupan pertama mengguncangkan alam,', 'yauma tarjufur-rājifah'),
(5718, 79, 7, 'تَتْبَعُهَا الرَّادِفَةُ ۗ', '(tiupan pertama) itu diiringi oleh tiupan kedua.', 'tatba\'uhar-rādifah'),
(5719, 79, 8, 'قُلُوْبٌ يَّوْمَىِٕذٍ وَّاجِفَةٌۙ', 'Hati manusia pada waktu itu merasa sangat takut,', 'qulụbuy yauma`iżiw wājifah'),
(5720, 79, 9, 'اَبْصَارُهَا خَاشِعَةٌ ۘ', 'pandangannya tunduk.', 'abṣāruhā khāsyi\'ah'),
(5721, 79, 10, 'يَقُوْلُوْنَ ءَاِنَّا لَمَرْدُوْدُوْنَ فِى الْحَافِرَةِۗ', '(Orang-orang kafir) berkata, “Apakah kita benar-benar akan dikembalikan kepada kehidupan yang semula?', 'yaqụlụna a innā lamardụdụna fil-ḥāfirah'),
(5722, 79, 11, 'ءَاِذَا كُنَّا عِظَامًا نَّخِرَةً ۗ', 'Apakah (akan dibangkitkan juga) apabila kita telah menjadi tulang belulang yang hancur?”', 'a iżā kunnā \'iẓāman nakhirah'),
(5723, 79, 12, 'قَالُوْا تِلْكَ اِذًا كَرَّةٌ خَاسِرَةٌ ۘ', 'Mereka berkata, “Kalau demikian, itu adalah suatu pengembalian yang merugikan.”', 'qālụ tilka iżang karratun khāsirah'),
(5724, 79, 13, 'فَاِنَّمَا هِيَ زَجْرَةٌ وَّاحِدَةٌۙ', 'Maka pengembalian itu hanyalah dengan sekali tiupan saja.', 'fa innamā hiya zajratuw wāḥidah'),
(5725, 79, 14, 'فَاِذَا هُمْ بِالسَّاهِرَةِۗ', 'Maka seketika itu mereka hidup kembali di bumi (yang baru).', 'fa iżā hum bis-sāhirah'),
(5726, 79, 15, 'هَلْ اَتٰىكَ حَدِيْثُ مُوْسٰىۘ', 'Sudahkah sampai kepadamu (Muhammad) kisah Musa?', 'hal atāka ḥadīṡu mụsā'),
(5727, 79, 16, 'اِذْ نَادٰىهُ رَبُّهٗ بِالْوَادِ الْمُقَدَّسِ طُوًىۚ', 'Ketika Tuhan memanggilnya (Musa) di lembah suci yaitu Lembah Tuwa;', 'iż nādāhu rabbuhụ bil-wādil-muqaddasi ṭuwā'),
(5728, 79, 17, 'اِذْهَبْ اِلٰى فِرْعَوْنَ اِنَّهٗ طَغٰىۖ', 'pergilah engkau kepada Fir‘aun! Sesungguhnya dia telah melampaui batas,', 'iż-hab ilā fir\'auna innahụ ṭagā'),
(5729, 79, 18, 'فَقُلْ هَلْ لَّكَ اِلٰٓى اَنْ تَزَكّٰىۙ', 'Maka katakanlah (kepada Fir‘aun), “Adakah keinginanmu untuk membersihkan diri (dari kesesatan),', 'fa qul hal laka ilā an tazakkā'),
(5730, 79, 19, 'وَاَهْدِيَكَ اِلٰى رَبِّكَ فَتَخْشٰىۚ', 'dan engkau akan kupimpin ke jalan Tuhanmu agar engkau takut kepada-Nya?”', 'wa ahdiyaka ilā rabbika fa takhsyā'),
(5731, 79, 20, 'فَاَرٰىهُ الْاٰيَةَ الْكُبْرٰىۖ', 'Lalu (Musa) memperlihatkan kepadanya mukjizat yang besar.', 'fa arāhul-āyatal-kubrā'),
(5732, 79, 21, 'فَكَذَّبَ وَعَصٰىۖ', 'Tetapi dia (Fir‘aun) mendustakan dan mendurhakai.', 'fa każżaba wa \'aṣā'),
(5733, 79, 22, 'ثُمَّ اَدْبَرَ يَسْعٰىۖ', 'Kemudian dia berpaling seraya berusaha menantang (Musa).', 'ṡumma adbara yas\'ā'),
(5734, 79, 23, 'فَحَشَرَ فَنَادٰىۖ', 'Kemudian dia mengumpulkan (pembesar-pembesarnya) lalu berseru (memanggil kaumnya).', 'fa ḥasyara fa nādā'),
(5735, 79, 24, 'فَقَالَ اَنَا۠ رَبُّكُمُ الْاَعْلٰىۖ', '(Seraya) berkata, “Akulah tuhanmu yang paling tinggi.”', 'fa qāla ana rabbukumul-a\'lā'),
(5736, 79, 25, 'فَاَخَذَهُ اللّٰهُ نَكَالَ الْاٰخِرَةِ وَالْاُوْلٰىۗ', 'Maka Allah menghukumnya dengan azab di akhirat dan siksaan di dunia.', 'fa akhażahullāhu nakālal-ākhirati wal-ụlā'),
(5737, 79, 26, 'اِنَّ فِيْ ذٰلِكَ لَعِبْرَةً لِّمَنْ يَّخْشٰى ۗ', 'Sungguh, pada yang demikian itu terdapat pelajaran bagi orang yang takut (kepada Allah).', 'inna fī żālika la\'ibratal limay yakhsyā'),
(5738, 79, 27, 'ءَاَنْتُمْ اَشَدُّ خَلْقًا اَمِ السَّمَاۤءُ ۚ بَنٰىهَاۗ', 'Apakah penciptaan kamu yang lebih hebat ataukah langit yang telah dibangun-Nya?', 'a antum asyaddu khalqan amis-samā`, banāhā'),
(5739, 79, 28, 'رَفَعَ سَمْكَهَا فَسَوّٰىهَاۙ', 'Dia telah meninggikan bangunannya lalu menyempurnakannya,', 'rafa\'a samkahā fa sawwāhā'),
(5740, 79, 29, 'وَاَغْطَشَ لَيْلَهَا وَاَخْرَجَ ضُحٰىهَاۖ', 'dan Dia menjadikan malamnya (gelap gulita), dan menjadikan siangnya (terang benderang).', 'wa agṭasya lailahā wa akhraja ḍuḥāhā'),
(5741, 79, 30, 'وَالْاَرْضَ بَعْدَ ذٰلِكَ دَحٰىهَاۗ', 'Dan setelah itu bumi Dia hamparkan.', 'wal-arḍa ba\'da żālika daḥāhā'),
(5742, 79, 31, 'اَخْرَجَ مِنْهَا مَاۤءَهَا وَمَرْعٰىهَاۖ', 'Darinya Dia pancarkan mata air, dan (ditumbuhkan) tumbuh-tumbuhannya.', 'akhraja min-hā mā`ahā wa mar\'āhā'),
(5743, 79, 32, 'وَالْجِبَالَ اَرْسٰىهَاۙ', 'Dan gunung-gunung Dia pancangkan dengan teguh.', 'wal-jibāla arsāhā'),
(5744, 79, 33, 'مَتَاعًا لَّكُمْ وَلِاَنْعَامِكُمْۗ', '(Semua itu) untuk kesenanganmu dan untuk hewan-hewan ternakmu.', 'matā\'al lakum wa li`an\'āmikum'),
(5745, 79, 34, 'فَاِذَا جَاۤءَتِ الطَّاۤمَّةُ الْكُبْرٰىۖ', 'Maka apabila malapetaka besar (hari Kiamat) telah datang,', 'fa iżā jā`atiṭ-ṭāmmatul-kubrā'),
(5746, 79, 35, 'يَوْمَ يَتَذَكَّرُ الْاِنْسَانُ مَا سَعٰىۙ', 'yaitu pada hari (ketika) manusia teringat akan apa yang telah dikerjakannya,', 'yauma yatażakkarul-insānu mā sa\'ā'),
(5747, 79, 36, 'وَبُرِّزَتِ الْجَحِيْمُ لِمَنْ يَّرٰى', 'dan neraka diperlihatkan dengan jelas kepada setiap orang yang melihat.', 'wa burrizatil-jaḥīmu limay yarā'),
(5748, 79, 37, 'فَاَمَّا مَنْ طَغٰىۖ', 'Maka adapun orang yang melampaui batas,', 'fa ammā man ṭagā'),
(5749, 79, 38, 'وَاٰثَرَ الْحَيٰوةَ الدُّنْيَاۙ', 'dan lebih mengutamakan kehidupan dunia,', 'wa āṡaral-ḥayātad-dun-yā'),
(5750, 79, 39, 'فَاِنَّ الْجَحِيْمَ هِيَ الْمَأْوٰىۗ', 'maka sungguh, nerakalah tempat tinggalnya.', 'fa innal-jaḥīma hiyal-ma`wā'),
(5751, 79, 40, 'وَاَمَّا مَنْ خَافَ مَقَامَ رَبِّهٖ وَنَهَى النَّفْسَ عَنِ الْهَوٰىۙ', 'Dan adapun orang-orang yang takut kepada kebesaran Tuhannya dan menahan diri dari (keinginan) hawa nafsunya,', 'wa ammā man khāfa maqāma rabbihī wa nahan-nafsa \'anil-hawā'),
(5752, 79, 41, 'فَاِنَّ الْجَنَّةَ هِيَ الْمَأْوٰىۗ', 'maka sungguh, surgalah tempat tinggal(nya).', 'fa innal-jannata hiyal-ma`wā'),
(5753, 79, 42, 'يَسْـَٔلُوْنَكَ عَنِ السَّاعَةِ اَيَّانَ مُرْسٰىهَاۗ', 'Mereka (orang-orang kafir) bertanya kepadamu (Muhammad) tentang hari Kiamat, “Kapankah terjadinya?”', 'yas`alụnaka \'anis-sā\'ati ayyāna mursāhā'),
(5754, 79, 43, 'فِيْمَ اَنْتَ مِنْ ذِكْرٰىهَاۗ', 'Untuk apa engkau perlu menyebutkannya (waktunya)?', 'fīma anta min żikrāhā'),
(5755, 79, 44, 'اِلٰى رَبِّكَ مُنْتَهٰىهَاۗ', 'Kepada Tuhanmulah (dikembalikan) kesudahannya (ketentuan waktunya).', 'ilā rabbika muntahāhā'),
(5756, 79, 45, 'اِنَّمَآ اَنْتَ مُنْذِرُ مَنْ يَّخْشٰىهَاۗ', 'Engkau (Muhammad) hanyalah pemberi peringatan bagi siapa yang takut kepadanya (hari Kiamat).', 'innamā anta munżiru may yakhsyāhā'),
(5757, 79, 46, 'كَاَنَّهُمْ يَوْمَ يَرَوْنَهَا لَمْ يَلْبَثُوْٓا اِلَّا عَشِيَّةً اَوْ ضُحٰىهَا', 'Pada hari ketika mereka melihat hari Kiamat itu (karena suasananya hebat), mereka merasa seakan-akan hanya (sebentar saja) tinggal (di dunia) pada waktu sore atau pagi hari.', 'ka`annahum yauma yaraunahā lam yalbaṡū illā \'asyiyyatan au ḍuḥāhā'),
(5758, 80, 1, 'عَبَسَ وَتَوَلّٰىٓۙ', 'Dia (Muhammad) berwajah masam dan berpaling,', '\'abasa wa tawallā'),
(5759, 80, 2, 'اَنْ جَاۤءَهُ الْاَعْمٰىۗ', 'karena seorang buta telah datang kepadanya (Abdullah bin Ummi Maktum).', 'an jā`ahul-a\'mā'),
(5760, 80, 3, 'وَمَا يُدْرِيْكَ لَعَلَّهٗ يَزَّكّٰىٓۙ', 'Dan tahukah engkau (Muhammad) barangkali dia ingin menyucikan dirinya (dari dosa),', 'wa mā yudrīka la\'allahụ yazzakkā'),
(5761, 80, 4, 'اَوْ يَذَّكَّرُ فَتَنْفَعَهُ الذِّكْرٰىۗ', 'atau dia (ingin) mendapatkan pengajaran, yang memberi manfaat kepadanya?', 'au yażżakkaru fa tanfa\'ahuż-żikrā'),
(5762, 80, 5, 'اَمَّا مَنِ اسْتَغْنٰىۙ', 'Adapun orang yang merasa dirinya serba cukup (pembesar-pembesar Quraisy),', 'ammā manistagnā'),
(5763, 80, 6, 'فَاَنْتَ لَهٗ تَصَدّٰىۗ', 'maka engkau (Muhammad) memberi perhatian kepadanya,', 'fa anta lahụ taṣaddā'),
(5764, 80, 7, 'وَمَا عَلَيْكَ اَلَّا يَزَّكّٰىۗ', 'padahal tidak ada (cela) atasmu kalau dia tidak menyucikan diri (beriman).', 'wa mā \'alaika allā yazzakkā'),
(5765, 80, 8, 'وَاَمَّا مَنْ جَاۤءَكَ يَسْعٰىۙ', 'Dan adapun orang yang datang kepadamu dengan bersegera (untuk mendapatkan pengajaran),', 'wa ammā man jā`aka yas\'ā'),
(5766, 80, 9, 'وَهُوَ يَخْشٰىۙ', 'sedang dia takut (kepada Allah),', 'wa huwa yakhsyā'),
(5767, 80, 10, 'فَاَنْتَ عَنْهُ تَلَهّٰىۚ', 'engkau (Muhammad) malah mengabaikannya.', 'fa anta \'an-hu talahhā'),
(5768, 80, 11, 'كَلَّآ اِنَّهَا تَذْكِرَةٌ ۚ', 'Sekali-kali jangan (begitu)! Sungguh, (ajaran-ajaran Allah) itu suatu peringatan,', 'kallā innahā tażkirah'),
(5769, 80, 12, 'فَمَنْ شَاۤءَ ذَكَرَهٗ ۘ', 'maka barangsiapa menghendaki, tentulah dia akan memperhatikannya,', 'fa man syā`a żakarah'),
(5770, 80, 13, 'فِيْ صُحُفٍ مُّكَرَّمَةٍۙ', 'di dalam kitab-kitab yang dimuliakan (di sisi Allah),', 'fī ṣuḥufim mukarramah'),
(5771, 80, 14, 'مَّرْفُوْعَةٍ مُّطَهَّرَةٍ ۢ ۙ', 'yang ditinggikan (dan) disucikan,', 'marfụ\'atim muṭahharah'),
(5772, 80, 15, 'بِاَيْدِيْ سَفَرَةٍۙ', 'di tangan para utusan (malaikat),', 'bi`aidī safarah'),
(5773, 80, 16, 'كِرَامٍۢ بَرَرَةٍۗ', 'yang mulia lagi berbakti.', 'kirāmim bararah'),
(5774, 80, 17, 'قُتِلَ الْاِنْسَانُ مَآ اَكْفَرَهٗۗ', 'Celakalah manusia! Alangkah kufurnya dia!', 'qutilal-insānu mā akfarah'),
(5775, 80, 18, 'مِنْ اَيِّ شَيْءٍ خَلَقَهٗۗ', 'Dari apakah Dia (Allah) menciptakannya?', 'min ayyi syai`in khalaqah'),
(5776, 80, 19, 'مِنْ نُّطْفَةٍۗ خَلَقَهٗ فَقَدَّرَهٗۗ', 'Dari setetes mani, Dia menciptakannya lalu menentukannya.', 'min nuṭfah, khalaqahụ fa qaddarah'),
(5777, 80, 20, 'ثُمَّ السَّبِيْلَ يَسَّرَهٗۙ', 'Kemudian jalannya Dia mudahkan,', 'ṡummas-sabīla yassarah'),
(5778, 80, 21, 'ثُمَّ اَمَاتَهٗ فَاَقْبَرَهٗۙ', 'kemudian Dia mematikannya lalu menguburkannya,', 'ṡumma amātahụ fa aqbarah'),
(5779, 80, 22, 'ثُمَّ اِذَا شَاۤءَ اَنْشَرَهٗۗ', 'kemudian jika Dia menghendaki, Dia membangkitkannya kembali.', 'ṡumma iżā syā`a ansyarah'),
(5780, 80, 23, 'كَلَّا لَمَّا يَقْضِ مَآ اَمَرَهٗۗ', 'Sekali-kali jangan (begitu)! Dia (manusia) itu belum melaksanakan apa yang Dia (Allah) perintahkan kepadanya.', 'kallā lammā yaqḍi mā amarah'),
(5781, 80, 24, 'فَلْيَنْظُرِ الْاِنْسَانُ اِلٰى طَعَامِهٖٓ ۙ', 'Maka hendaklah manusia itu memperhatikan makanannya.', 'falyanẓuril-insānu ilā ṭa\'āmih'),
(5782, 80, 25, 'اَنَّا صَبَبْنَا الْمَاۤءَ صَبًّاۙ', 'Kamilah yang telah mencurahkan air melimpah (dari langit),', 'annā ṣababnal-mā`a ṣabbā'),
(5783, 80, 26, 'ثُمَّ شَقَقْنَا الْاَرْضَ شَقًّاۙ', 'kemudian Kami belah bumi dengan sebaik-baiknya,', 'ṡumma syaqaqnal-arḍa syaqqā'),
(5784, 80, 27, 'فَاَنْۢبَتْنَا فِيْهَا حَبًّاۙ', 'lalu di sana Kami tumbuhkan biji-bijian,', 'fa ambatnā fīhā ḥabbā'),
(5785, 80, 28, 'وَّعِنَبًا وَّقَضْبًاۙ', 'dan anggur dan sayur-sayuran,', 'wa \'inabaw wa qaḍbā'),
(5786, 80, 29, 'وَّزَيْتُوْنًا وَّنَخْلًاۙ', 'dan zaitun dan pohon kurma,', 'wa zaitụnaw wa nakhlā'),
(5787, 80, 30, 'وَّحَدَاۤئِقَ غُلْبًا', 'dan kebun-kebun (yang) rindang,', 'wa ḥadā`iqa gulbā'),
(5788, 80, 31, 'وَفَاكِهَةً وَّاَبًّا', 'dan buah-buahan serta rerumputan.', 'wa fākihataw wa abbā'),
(5789, 80, 32, 'مَتَاعًا لَّكُمْ وَلِاَنْعَامِكُمْۗ', '(Semua itu) untuk kesenanganmu dan untuk hewan-hewan ternakmu.', 'matā\'al lakum wa li`an\'āmikum'),
(5790, 80, 33, 'فَاِذَا جَاۤءَتِ الصَّاۤخَّةُ ۖ', 'Maka apabila datang suara yang memekakkan (tiupan sangkakala yang kedua),', 'fa iżā jā`atiṣ-ṣākhkhah'),
(5791, 80, 34, 'يَوْمَ يَفِرُّ الْمَرْءُ مِنْ اَخِيْهِۙ', 'pada hari itu manusia lari dari saudaranya,', 'yauma yafirrul-mar`u min akhīh'),
(5792, 80, 35, 'وَاُمِّهٖ وَاَبِيْهِۙ', 'dan dari ibu dan bapaknya,', 'wa ummihī wa abīh'),
(5793, 80, 36, 'وَصَاحِبَتِهٖ وَبَنِيْهِۗ', 'dan dari istri dan anak-anaknya.', 'wa ṣāḥibatihī wa banīh'),
(5794, 80, 37, 'لِكُلِّ امْرِئٍ مِّنْهُمْ يَوْمَىِٕذٍ شَأْنٌ يُّغْنِيْهِۗ', 'Setiap orang dari mereka pada hari itu mempunyai urusan yang menyibukkannya.', 'likullimri`im min-hum yauma`iżin sya`nuy yugnīh'),
(5795, 80, 38, 'وُجُوْهٌ يَّوْمَىِٕذٍ مُّسْفِرَةٌۙ', 'Pada hari itu ada wajah-wajah yang berseri-seri,', 'wujụhuy yauma`iżim musfirah'),
(5796, 80, 39, 'ضَاحِكَةٌ مُّسْتَبْشِرَةٌ ۚ', 'tertawa dan gembira ria,', 'ḍāḥikatum mustabsyirah'),
(5797, 80, 40, 'وَوُجُوْهٌ يَّوْمَىِٕذٍ عَلَيْهَا غَبَرَةٌۙ', 'dan pada hari itu ada (pula) wajah-wajah yang tertutup debu (suram),', 'wa wujụhuy yauma`iżin \'alaihā gabarah'),
(5798, 80, 41, 'تَرْهَقُهَا قَتَرَةٌ ۗ', 'tertutup oleh kegelapan (ditimpa kehinaan dan kesusahan).', 'tarhaquhā qatarah'),
(5799, 80, 42, 'اُولٰۤىِٕكَ هُمُ الْكَفَرَةُ الْفَجَرَةُ', 'Mereka itulah orang-orang kafir yang durhaka.', 'ulā`ika humul-kafaratul-fajarah'),
(5800, 81, 1, 'اِذَا الشَّمْسُ كُوِّرَتْۖ', 'Apabila matahari digulung,', 'iżasy-syamsu kuwwirat'),
(5801, 81, 2, 'وَاِذَا النُّجُوْمُ انْكَدَرَتْۖ', 'dan apabila bintang-bintang berjatuhan,', 'wa iżan-nujụmungkadarat'),
(5802, 81, 3, 'وَاِذَا الْجِبَالُ سُيِّرَتْۖ', 'dan apabila gunung-gunung dihancurkan,', 'wa iżal-jibālu suyyirat'),
(5803, 81, 4, 'وَاِذَا الْعِشَارُ عُطِّلَتْۖ', 'dan apabila unta-unta yang bunting ditinggalkan (tidak terurus),', 'wa iżal-\'isyāru \'uṭṭilat'),
(5804, 81, 5, 'وَاِذَا الْوُحُوْشُ حُشِرَتْۖ', 'dan apabila binatang-binatang liar dikumpulkan,', 'wa iżal-wuḥụsyu ḥusyirat'),
(5805, 81, 6, 'وَاِذَا الْبِحَارُ سُجِّرَتْۖ', 'dan apabila lautan dipanaskan,', 'wa iżal-biḥāru sujjirat'),
(5806, 81, 7, 'وَاِذَا النُّفُوْسُ زُوِّجَتْۖ', 'dan apabila roh-roh dipertemukan (dengan tubuh),', 'wa iżan-nufụsu zuwwijat'),
(5807, 81, 8, 'وَاِذَا الْمَوْءٗدَةُ سُىِٕلَتْۖ', 'dan apabila bayi-bayi perempuan yang dikubur hidup-hidup ditanya,', 'wa iżal-mau`ụdatu su`ilat'),
(5808, 81, 9, 'بِاَيِّ ذَنْۢبٍ قُتِلَتْۚ', 'karena dosa apa dia dibunuh?', 'bi`ayyi żambing qutilat'),
(5809, 81, 10, 'وَاِذَا الصُّحُفُ نُشِرَتْۖ', 'Dan apabila lembaran-lembaran (catatan amal) telah dibuka lebar-lebar,', 'wa iżaṣ-ṣuḥufu nusyirat'),
(5810, 81, 11, 'وَاِذَا السَّمَاۤءُ كُشِطَتْۖ', 'dan apabila langit dilenyapkan,', 'wa iżas-samā`u kusyiṭat'),
(5811, 81, 12, 'وَاِذَا الْجَحِيْمُ سُعِّرَتْۖ', 'dan apabila neraka Jahim dinyalakan,', 'wa iżal-jaḥīmu su\'\'irat'),
(5812, 81, 13, 'وَاِذَا الْجَنَّةُ اُزْلِفَتْۖ', 'dan apabila surga didekatkan,', 'wa iżal-jannatu uzlifat'),
(5813, 81, 14, 'عَلِمَتْ نَفْسٌ مَّآ اَحْضَرَتْۗ', 'setiap jiwa akan mengetahui apa yang telah dikerjakannya.', '\'alimat nafsum mā aḥḍarat'),
(5814, 81, 15, 'فَلَآ اُقْسِمُ بِالْخُنَّسِۙ', 'Aku bersumpah demi bintang-bintang,', 'fa lā uqsimu bil-khunnas'),
(5815, 81, 16, 'الْجَوَارِ الْكُنَّسِۙ', 'yang beredar dan terbenam,', 'al-jawāril-kunnas'),
(5816, 81, 17, 'وَالَّيْلِ اِذَا عَسْعَسَۙ', 'demi malam apabila telah larut,', 'wal-laili iżā \'as\'as'),
(5817, 81, 18, 'وَالصُّبْحِ اِذَا تَنَفَّسَۙ', 'dan demi subuh apabila fajar telah menyingsing,', 'waṣ-ṣub-ḥi iżā tanaffas'),
(5818, 81, 19, 'اِنَّهٗ لَقَوْلُ رَسُوْلٍ كَرِيْمٍۙ', 'sesungguhnya (Al-Qur\'an) itu benar-benar firman (Allah yang dibawa oleh) utusan yang mulia (Jibril),', 'innahụ laqaulu rasụling karīm'),
(5819, 81, 20, 'ذِيْ قُوَّةٍ عِنْدَ ذِى الْعَرْشِ مَكِيْنٍۙ', 'yang memiliki kekuatan, memiliki kedudukan tinggi di sisi (Allah) yang memiliki ‘Arsy,', 'żī quwwatin \'inda żil-\'arsyi makīn'),
(5820, 81, 21, 'مُّطَاعٍ ثَمَّ اَمِيْنٍۗ', 'yang di sana (di alam malaikat) ditaati dan dipercaya.', 'muṭā\'in ṡamma amīn'),
(5821, 81, 22, 'وَمَا صَاحِبُكُمْ بِمَجْنُوْنٍۚ', 'Dan temanmu (Muhammad) itu bukanlah orang gila.', 'wa mā ṣāḥibukum bimajnụn'),
(5822, 81, 23, 'وَلَقَدْ رَاٰهُ بِالْاُفُقِ الْمُبِيْنِۚ', 'Dan sungguh, dia (Muhammad) telah melihatnya (Jibril) di ufuk yang terang.', 'wa laqad ra`āhu bil-ufuqil-mubīn'),
(5823, 81, 24, 'وَمَا هُوَ عَلَى الْغَيْبِ بِضَنِيْنٍۚ', 'Dan dia (Muhammad) bukanlah seorang yang kikir (enggan) untuk menerangkan yang gaib.', 'wa mā huwa \'alal-gaibi biḍanīn'),
(5824, 81, 25, 'وَمَا هُوَ بِقَوْلِ شَيْطٰنٍ رَّجِيْمٍۚ', 'Dan (Al-Qur\'an) itu bukanlah perkataan setan yang terkutuk,', 'wa mā huwa biqauli syaiṭānir rajīm'),
(5825, 81, 26, 'فَاَيْنَ تَذْهَبُوْنَۗ', 'maka ke manakah kamu akan pergi?', 'fa aina taż-habụn'),
(5826, 81, 27, 'اِنْ هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَۙ', '(Al-Qur\'an) itu tidak lain adalah peringatan bagi seluruh alam,', 'in huwa illā żikrul lil-\'ālamīn'),
(5827, 81, 28, 'لِمَنْ شَاۤءَ مِنْكُمْ اَنْ يَّسْتَقِيْمَۗ', '(yaitu) bagi siapa di antara kamu yang menghendaki menempuh jalan yang lurus.', 'liman syā`a mingkum ay yastaqīm'),
(5828, 81, 29, 'وَمَا تَشَاۤءُوْنَ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ رَبُّ الْعٰلَمِيْنَ', 'Dan kamu tidak dapat menghendaki (menempuh jalan itu) kecuali apabila dikehendaki Allah, Tuhan seluruh alam.', 'wa mā tasyā`ụna illā ay yasyā`allāhu rabbul-\'ālamīn'),
(5829, 82, 1, 'اِذَا السَّمَاۤءُ انْفَطَرَتْۙ', 'Apabila langit terbelah,', 'iżas-samā`unfaṭarat'),
(5830, 82, 2, 'وَاِذَا الْكَوَاكِبُ انْتَثَرَتْۙ', 'dan apabila bintang-bintang jatuh berserakan,', 'wa iżal-kawākibuntaṡarat'),
(5831, 82, 3, 'وَاِذَا الْبِحَارُ فُجِّرَتْۙ', 'dan apabila lautan dijadikan meluap,', 'wa iżal-biḥāru fujjirat'),
(5832, 82, 4, 'وَاِذَا الْقُبُوْرُ بُعْثِرَتْۙ', 'dan apabila kuburan-kuburan dibongkar,', 'wa iżal-qubụru bu\'ṡirat'),
(5833, 82, 5, 'عَلِمَتْ نَفْسٌ مَّا قَدَّمَتْ وَاَخَّرَتْۗ', '(maka) setiap jiwa akan mengetahui apa yang telah dikerjakan dan yang dilalaikan(nya).', '\'alimat nafsum mā qaddamat wa akhkharat'),
(5834, 82, 6, 'يٰٓاَيُّهَا الْاِنْسَانُ مَا غَرَّكَ بِرَبِّكَ الْكَرِيْمِۙ', 'Wahai manusia! Apakah yang telah memperdayakan kamu (berbuat durhaka) terhadap Tuhanmu Yang Mahamulia,', 'yā ayyuhal-insānu mā garraka birabbikal-karīm'),
(5835, 82, 7, 'الَّذِيْ خَلَقَكَ فَسَوّٰىكَ فَعَدَلَكَۙ', 'yang telah menciptakanmu lalu menyempurnakan kejadianmu dan menjadikan (susunan tubuh)mu seimbang,', 'allażī khalaqaka fa sawwāka fa \'adalak'),
(5836, 82, 8, 'فِيْٓ اَيِّ صُوْرَةٍ مَّا شَاۤءَ رَكَّبَكَۗ', 'dalam bentuk apa saja yang dikehendaki, Dia menyusun tubuhmu.', 'fī ayyi ṣụratim mā syā`a rakkabak'),
(5837, 82, 9, 'كَلَّا بَلْ تُكَذِّبُوْنَ بِالدِّيْنِۙ', 'Sekali-kali jangan begitu! Bahkan kamu mendustakan hari pembalasan.', 'kallā bal tukażżibụna bid-dīn'),
(5838, 82, 10, 'وَاِنَّ عَلَيْكُمْ لَحٰفِظِيْنَۙ', 'Dan sesungguhnya bagi kamu ada (malaikat-malaikat) yang mengawasi (pekerjaanmu),', 'wa inna \'alaikum laḥāfiẓīn'),
(5839, 82, 11, 'كِرَامًا كَاتِبِيْنَۙ', 'yang mulia (di sisi Allah) dan yang mencatat (amal perbuatanmu),', 'kirāmang kātibīn'),
(5840, 82, 12, 'يَعْلَمُوْنَ مَا تَفْعَلُوْنَ', 'mereka mengetahui apa yang kamu kerjakan.', 'ya\'lamụna mā taf\'alụn'),
(5841, 82, 13, 'اِنَّ الْاَبْرَارَ لَفِيْ نَعِيْمٍۙ', 'Sesungguhnya orang-orang yang berbakti benar-benar berada dalam (surga yang penuh) kenikmatan,', 'innal-abrāra lafī na\'īm'),
(5842, 82, 14, 'وَّاِنَّ الْفُجَّارَ لَفِيْ جَحِيْمٍ', 'dan sesungguhnya orang-orang yang durhaka benar-benar berada dalam neraka.', 'wa innal-fujjāra lafī jaḥīm'),
(5843, 82, 15, 'يَصْلَوْنَهَا يَوْمَ الدِّيْنِ', 'Mereka masuk ke dalamnya pada hari pembalasan.', 'yaṣlaunahā yaumad-dīn'),
(5844, 82, 16, 'وَمَا هُمْ عَنْهَا بِغَاۤىِٕبِيْنَۗ', 'Dan mereka tidak mungkin keluar dari neraka itu.', 'wa mā hum \'an-hā bigā`ibīn'),
(5845, 82, 17, 'وَمَآ اَدْرٰىكَ مَا يَوْمُ الدِّيْنِۙ', 'Dan tahukah kamu apakah hari pembalasan itu?', 'wa mā adrāka mā yaumud-dīn'),
(5846, 82, 18, 'ثُمَّ مَآ اَدْرٰىكَ مَا يَوْمُ الدِّيْنِۗ', 'Sekali lagi, tahukah kamu apakah hari pembalasan itu?', 'ṡumma mā adrāka mā yaumud-dīn'),
(5847, 82, 19, 'يَوْمَ لَا تَمْلِكُ نَفْسٌ لِّنَفْسٍ شَيْـًٔا ۗوَالْاَمْرُ يَوْمَىِٕذٍ لِّلّٰهِ', '(Yaitu) pada hari (ketika) seseorang sama sekali tidak berdaya (menolong) orang lain. Dan segala urusan pada hari itu dalam kekuasaan Allah.', 'yauma lā tamliku nafsul linafsin syai`ā, wal-amru yauma`iżil lillāh'),
(5848, 83, 1, 'وَيْلٌ لِّلْمُطَفِّفِيْنَۙ', 'Celakalah bagi orang-orang yang curang (dalam menakar dan menimbang)!', 'wailul lil-muṭaffifīn'),
(5849, 83, 2, 'الَّذِيْنَ اِذَا اكْتَالُوْا عَلَى النَّاسِ يَسْتَوْفُوْنَۖ', '(Yaitu) orang-orang yang apabila menerima takaran dari orang lain mereka minta dicukupkan,', 'allażīna iżaktālụ \'alan-nāsi yastaufụn'),
(5850, 83, 3, 'وَاِذَا كَالُوْهُمْ اَوْ وَّزَنُوْهُمْ يُخْسِرُوْنَۗ', 'dan apabila mereka menakar atau menimbang (untuk orang lain), mereka mengurangi.', 'wa iżā kālụhum aw wazanụhum yukhsirụn'),
(5851, 83, 4, 'اَلَا يَظُنُّ اُولٰۤىِٕكَ اَنَّهُمْ مَّبْعُوْثُوْنَۙ', 'Tidakkah mereka itu mengira, bahwa sesungguhnya mereka akan dibangkitkan,', 'alā yaẓunnu ulā`ika annahum mab\'ụṡụn'),
(5852, 83, 5, 'لِيَوْمٍ عَظِيْمٍۙ', 'pada suatu hari yang besar,', 'liyaumin \'aẓīm'),
(5853, 83, 6, 'يَّوْمَ يَقُوْمُ النَّاسُ لِرَبِّ الْعٰلَمِيْنَۗ', '(yaitu) pada hari (ketika) semua orang bangkit menghadap Tuhan seluruh alam.', 'yauma yaqụmun-nāsu lirabbil-\'ālamīn'),
(5854, 83, 7, 'كَلَّآ اِنَّ كِتٰبَ الْفُجَّارِ لَفِيْ سِجِّيْنٍۗ', 'Sekali-kali jangan begitu! Sesungguhnya catatan orang yang durhaka benar-benar tersimpan dalam Sijjin.', 'kallā inna kitābal-fujjāri lafī sijjīn'),
(5855, 83, 8, 'وَمَآ اَدْرٰىكَ مَا سِجِّيْنٌۗ', 'Dan tahukah engkau apakah Sijjin itu?', 'wa mā adrāka mā sijjīn'),
(5856, 83, 9, 'كِتٰبٌ مَّرْقُوْمٌۗ', '(Yaitu) Kitab yang berisi catatan (amal).', 'kitābum marqụm'),
(5857, 83, 10, 'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَۙ', 'Celakalah pada hari itu, bagi orang-orang yang mendustakan!', 'wailuy yauma`iżil lil-mukażżibīn'),
(5858, 83, 11, 'الَّذِيْنَ يُكَذِّبُوْنَ بِيَوْمِ الدِّيْنِۗ', '(yaitu) orang-orang yang mendustakannya (hari pembalasan).', 'allażīna yukażżibụna biyaumid-dīn'),
(5859, 83, 12, 'وَمَا يُكَذِّبُ بِهٖٓ اِلَّا كُلُّ مُعْتَدٍ اَثِيْمٍۙ', 'Dan tidak ada yang mendustakannya (hari pembalasan) kecuali setiap orang yang melampaui batas dan berdosa,', 'wa mā yukażżibu bihī illā kullu mu\'tadin aṡīm'),
(5860, 83, 13, 'اِذَا تُتْلٰى عَلَيْهِ اٰيٰتُنَا قَالَ اَسَاطِيْرُ الْاَوَّلِيْنَۗ', 'yang apabila dibacakan kepadanya ayat-ayat Kami, dia berkata, “Itu adalah dongeng orang-orang dahulu.”', 'iżā tutlā \'alaihi āyātunā qāla asāṭīrul-awwalīn'),
(5861, 83, 14, 'كَلَّا بَلْ ۜرَانَ عَلٰى قُلُوْبِهِمْ مَّا كَانُوْا يَكْسِبُوْنَ', 'Sekali-kali tidak! Bahkan apa yang mereka kerjakan itu telah menutupi hati mereka.', 'kallā bal rāna \'alā qulụbihim mā kānụ yaksibụn'),
(5862, 83, 15, 'كَلَّآ اِنَّهُمْ عَنْ رَّبِّهِمْ يَوْمَىِٕذٍ لَّمَحْجُوْبُوْنَۗ', 'Sekali-kali tidak! Sesungguhnya mereka pada hari itu benar-benar terhalang dari (melihat) Tuhannya.', 'kallā innahum \'ar rabbihim yauma`iżil lamaḥjụbụn'),
(5863, 83, 16, 'ثُمَّ اِنَّهُمْ لَصَالُوا الْجَحِيْمِۗ', 'Kemudian, sesungguhnya mereka benar-benar masuk neraka.', 'ṡumma innahum laṣālul-jaḥīm'),
(5864, 83, 17, 'ثُمَّ يُقَالُ هٰذَا الَّذِيْ كُنْتُمْ بِهٖ تُكَذِّبُوْنَۗ', 'Kemudian, dikatakan (kepada mereka), “Inilah (azab) yang dahulu kamu dustakan.”', 'ṡumma yuqālu hāżallażī kuntum bihī tukażżibụn'),
(5865, 83, 18, 'كَلَّآ اِنَّ كِتٰبَ الْاَبْرَارِ لَفِيْ عِلِّيِّيْنَۗ', 'Sekali-kali tidak! Sesungguhnya catatan orang-orang yang berbakti benar-benar tersimpan dalam ’Illiyyin.', 'kallā inna kitābal-abrāri lafī \'illiyyīn'),
(5866, 83, 19, 'وَمَآ اَدْرٰىكَ مَا عِلِّيُّوْنَۗ', 'Dan tahukah engkau apakah ’Illiyyin itu?', 'wa mā adrāka mā \'illiyyụn'),
(5867, 83, 20, 'كِتٰبٌ مَّرْقُوْمٌۙ', '(Yaitu) Kitab yang berisi catatan (amal),', 'kitābum marqụm'),
(5868, 83, 21, 'يَّشْهَدُهُ الْمُقَرَّبُوْنَۗ', 'yang disaksikan oleh (malaikat-malaikat) yang didekatkan (kepada Allah).', 'yasy-haduhul-muqarrabụn'),
(5869, 83, 22, 'اِنَّ الْاَبْرَارَ لَفِيْ نَعِيْمٍۙ', 'Sesungguhnya orang-orang yang berbakti benar-benar berada dalam (surga yang penuh) kenikmatan,', 'innal-abrāra lafī na\'īm'),
(5870, 83, 23, 'عَلَى الْاَرَاۤىِٕكِ يَنْظُرُوْنَۙ', 'mereka (duduk) di atas dipan-dipan melepas pandangan.', '\'alal-arā`iki yanẓurụn'),
(5871, 83, 24, 'تَعْرِفُ فِيْ وُجُوْهِهِمْ نَضْرَةَ النَّعِيْمِۚ', 'Kamu dapat mengetahui dari wajah mereka kesenangan hidup yang penuh kenikmatan.', 'ta\'rifu fī wujụhihim naḍratan na\'īm'),
(5872, 83, 25, 'يُسْقَوْنَ مِنْ رَّحِيْقٍ مَّخْتُوْمٍۙ', 'Mereka diberi minum dari khamar murni (tidak memabukkan) yang (tempatnya) masih dilak (disegel),', 'yusqauna mir raḥīqim makhtụm'),
(5873, 83, 26, 'خِتٰمُهٗ مِسْكٌ ۗوَفِيْ ذٰلِكَ فَلْيَتَنَافَسِ الْمُتَنَافِسُوْنَۗ', 'laknya dari kasturi. Dan untuk yang demikian itu hendaknya orang berlomba-lomba.', 'khitāmuhụ misk, wa fī żālika falyatanāfasil-mutanāfisụn'),
(5874, 83, 27, 'وَمِزَاجُهٗ مِنْ تَسْنِيْمٍۙ', 'Dan campurannya dari tasnim,', 'wa mizājuhụ min tasnīm'),
(5875, 83, 28, 'عَيْنًا يَّشْرَبُ بِهَا الْمُقَرَّبُوْنَۗ', '(yaitu) mata air yang diminum oleh mereka yang dekat (kepada Allah).', '\'ainay yasyrabu bihal-muqarrabụn'),
(5876, 83, 29, 'اِنَّ الَّذِيْنَ اَجْرَمُوْا كَانُوْا مِنَ الَّذِيْنَ اٰمَنُوْا يَضْحَكُوْنَۖ', 'Sesungguhnya orang-orang yang berdosa adalah mereka yang dahulu menertawakan orang-orang yang beriman.', 'innallażīna ajramụ kānụ minallażīna āmanụ yaḍ-ḥakụn'),
(5877, 83, 30, 'وَاِذَا مَرُّوْا بِهِمْ يَتَغَامَزُوْنَۖ', 'Dan apabila mereka (orang-orang yang beriman) melintas di hadapan mereka, mereka saling mengedip-ngedipkan matanya,', 'wa iżā marrụ bihim yatagāmazụn'),
(5878, 83, 31, 'وَاِذَا انْقَلَبُوْٓا اِلٰٓى اَهْلِهِمُ انْقَلَبُوْا فَكِهِيْنَۖ', 'dan apabila kembali kepada kaumnya, mereka kembali dengan gembira ria.', 'wa iżangqalabū ilā ahlihimungqalabụ fakihīn'),
(5879, 83, 32, 'وَاِذَا رَاَوْهُمْ قَالُوْٓا اِنَّ هٰٓؤُلَاۤءِ لَضَاۤلُّوْنَۙ', 'Dan apabila mereka melihat (orang-orang mukmin), mereka mengatakan, “Sesungguhnya mereka benar-benar orang-orang sesat,”', 'wa iżā ra`auhum qālū inna hā`ulā`i laḍāllụn'),
(5880, 83, 33, 'وَمَآ اُرْسِلُوْا عَلَيْهِمْ حٰفِظِيْنَۗ', 'padahal (orang-orang yang berdosa itu), mereka tidak diutus sebagai penjaga (orang-orang mukmin).', 'wa mā ursilụ \'alaihim ḥāfiẓīn'),
(5881, 83, 34, 'فَالْيَوْمَ الَّذِيْنَ اٰمَنُوْا مِنَ الْكُفَّارِ يَضْحَكُوْنَۙ', 'Maka pada hari ini, orang-orang yang beriman yang menertawakan orang-orang kafir,', 'fal-yaumallażīna āmanụ minal-kuffāri yaḍ-ḥakụn'),
(5882, 83, 35, 'عَلَى الْاَرَاۤىِٕكِ يَنْظُرُوْنَۗ', 'mereka (duduk) di atas dipan-dipan melepas pandangan.', '\'alal-arā`iki yanẓurụn'),
(5883, 83, 36, 'هَلْ ثُوِّبَ الْكُفَّارُ مَا كَانُوْا يَفْعَلُوْنَ', 'Apakah orang-orang kafir itu diberi balasan (hukuman) terhadap apa yang telah mereka perbuat?', 'hal ṡuwwibal-kuffāru mā kānụ yaf\'alụn'),
(5884, 84, 1, 'اِذَا السَّمَاۤءُ انْشَقَّتْۙ', 'Apabila langit terbelah,', 'iżas-samā`unsyaqqat'),
(5885, 84, 2, 'وَاَذِنَتْ لِرَبِّهَا وَحُقَّتْۙ', 'dan patuh kepada Tuhannya, dan sudah semestinya patuh,', 'wa ażinat lirabbihā wa ḥuqqat'),
(5886, 84, 3, 'وَاِذَا الْاَرْضُ مُدَّتْۙ', 'dan apabila bumi diratakan,', 'wa iżal-arḍu muddat'),
(5887, 84, 4, 'وَاَلْقَتْ مَا فِيْهَا وَتَخَلَّتْۙ', 'dan memuntahkan apa yang ada di dalamnya dan menjadi kosong,', 'wa alqat mā fīhā wa takhallat'),
(5888, 84, 5, 'وَاَذِنَتْ لِرَبِّهَا وَحُقَّتْۗ', 'dan patuh kepada Tuhannya, dan sudah semestinya patuh.', 'wa ażinat lirabbihā wa ḥuqqat'),
(5889, 84, 6, 'يٰٓاَيُّهَا الْاِنْسَانُ اِنَّكَ كَادِحٌ اِلٰى رَبِّكَ كَدْحًا فَمُلٰقِيْهِۚ', 'Wahai manusia! Sesungguhnya kamu telah bekerja keras menuju Tuhanmu, maka kamu akan menemui-Nya.', 'yā ayyuhal-insānu innaka kādiḥun ilā rabbika kad-ḥan fa mulāqīh'),
(5890, 84, 7, 'فَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ بِيَمِيْنِهٖۙ', 'Maka adapun orang yang catatannya diberikan dari sebelah kanannya,', 'fa ammā man ụtiya kitābahụ biyamīnih'),
(5891, 84, 8, 'فَسَوْفَ يُحَاسَبُ حِسَابًا يَّسِيْرًاۙ', 'maka dia akan diperiksa dengan pemeriksaan yang mudah,', 'fa saufa yuḥāsabu ḥisābay yasīrā'),
(5892, 84, 9, 'وَّيَنْقَلِبُ اِلٰٓى اَهْلِهٖ مَسْرُوْرًاۗ', 'dan dia akan kembali kepada keluarganya (yang sama-sama beriman) dengan gembira.', 'wa yangqalibu ilā ahlihī masrụrā'),
(5893, 84, 10, 'وَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ وَرَاۤءَ ظَهْرِهٖۙ', 'Dan adapun orang yang catatannya diberikan dari sebelah belakang,', 'wa ammā man ụtiya kitābahụ warā`a ẓahrih'),
(5894, 84, 11, 'فَسَوْفَ يَدْعُوْ ثُبُوْرًاۙ', 'maka dia akan berteriak, “Celakalah aku!”', 'fa saufa yad\'ụ ṡubụrā'),
(5895, 84, 12, 'وَّيَصْلٰى سَعِيْرًاۗ', 'Dan dia akan masuk ke dalam api yang menyala-nyala (neraka).', 'wa yaṣlā sa\'īrā'),
(5896, 84, 13, 'اِنَّهٗ كَانَ فِيْٓ اَهْلِهٖ مَسْرُوْرًاۗ', 'Sungguh, dia dahulu (di dunia) bergembira di kalangan keluarganya (yang sama-sama kafir).', 'innahụ kāna fī ahlihī masrụrā'),
(5897, 84, 14, 'اِنَّهٗ ظَنَّ اَنْ لَّنْ يَّحُوْرَ ۛ', 'Sesungguhnya dia mengira bahwa dia tidak akan kembali (kepada Tuhannya).', 'innahụ ẓanna al lay yaḥụr'),
(5898, 84, 15, 'بَلٰىۛ اِنَّ رَبَّهٗ كَانَ بِهٖ بَصِيْرًاۗ', 'Tidak demikian, sesungguhnya Tuhannya selalu melihatnya.', 'balā inna rabbahụ kāna bihī baṣīrā'),
(5899, 84, 16, 'فَلَآ اُقْسِمُ بِالشَّفَقِۙ', 'Maka Aku bersumpah demi cahaya merah pada waktu senja,', 'fa lā uqsimu bisy-syafaq'),
(5900, 84, 17, 'وَالَّيْلِ وَمَا وَسَقَۙ', 'demi malam dan apa yang diselubunginya,', 'wal-laili wa mā wasaq'),
(5901, 84, 18, 'وَالْقَمَرِ اِذَا اتَّسَقَۙ', 'demi bulan apabila jadi purnama,', 'wal-qamari iżattasaq'),
(5902, 84, 19, 'لَتَرْكَبُنَّ طَبَقًا عَنْ طَبَقٍۗ', 'sungguh, akan kamu jalani tingkat demi tingkat (dalam kehidupan).', 'latarkabunna ṭabaqan \'an ṭabaq'),
(5903, 84, 20, 'فَمَا لَهُمْ لَا يُؤْمِنُوْنَۙ', 'Maka mengapa mereka tidak mau beriman?', 'fa mā lahum lā yu`minụn'),
(5904, 84, 21, 'وَاِذَا قُرِئَ عَلَيْهِمُ الْقُرْاٰنُ لَا يَسْجُدُوْنَ ۗ ۩', 'Dan apabila Al-Qur\'an dibacakan kepada mereka, mereka tidak (mau) bersujud,', 'wa iżā quri`a \'alaihimul-qur`ānu lā yasjudụn'),
(5905, 84, 22, 'بَلِ الَّذِيْنَ كَفَرُوْا يُكَذِّبُوْنَۖ', 'bahkan orang-orang kafir itu mendustakan(nya).', 'balillażīna kafarụ yukażżibụn'),
(5906, 84, 23, 'وَاللّٰهُ اَعْلَمُ بِمَا يُوْعُوْنَۖ', 'Dan Allah lebih mengetahui apa yang mereka sembunyikan (dalam hati mereka).', 'wallāhu a\'lamu bimā yụ\'ụn'),
(5907, 84, 24, 'فَبَشِّرْهُمْ بِعَذَابٍ اَلِيْمٍۙ', 'Maka sampaikanlah kepada mereka (ancaman) azab yang pedih,', 'fa basysyir-hum bi\'ażābin alīm'),
(5908, 84, 25, 'اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ اَجْرٌ غَيْرُ مَمْنُوْنٍ', 'kecuali orang-orang yang beriman dan mengerjakan kebajikan, mereka akan mendapat pahala yang tidak putus-putusnya.', 'illallażīna āmanụ wa \'amiluṣ-ṣāliḥāti lahum ajrun gairu mamnụn'),
(5909, 85, 1, 'وَالسَّمَاۤءِ ذَاتِ الْبُرُوْجِۙ', 'Demi langit yang mempunyai gugusan bintang,', 'was-samā`i żātil-burụj'),
(5910, 85, 2, 'وَالْيَوْمِ الْمَوْعُوْدِۙ', 'dan demi hari yang dijanjikan.', 'wal-yaumil-mau\'ụd'),
(5911, 85, 3, 'وَشَاهِدٍ وَّمَشْهُوْدٍۗ', 'Demi yang menyaksikan dan yang disaksikan.', 'wa syāhidiw wa masy-hụd'),
(5912, 85, 4, 'قُتِلَ اَصْحٰبُ الْاُخْدُوْدِۙ', 'Binasalah orang-orang yang membuat parit (yaitu para pembesar Najran di Yaman),', 'qutila aṣ-ḥābul-ukhdụd'),
(5913, 85, 5, 'النَّارِ ذَاتِ الْوَقُوْدِۙ', 'yang berapi (yang mempunyai) kayu bakar,', 'an-nāri żātil-waqụd'),
(5914, 85, 6, 'اِذْ هُمْ عَلَيْهَا قُعُوْدٌۙ', 'ketika mereka duduk di sekitarnya,', 'iż hum \'alaihā qu\'ụd'),
(5915, 85, 7, 'وَّهُمْ عَلٰى مَا يَفْعَلُوْنَ بِالْمُؤْمِنِيْنَ شُهُوْدٌ ۗ', 'sedang mereka menyaksikan apa yang mereka perbuat terhadap orang-orang mukmin.', 'wa hum \'alā mā yaf\'alụna bil-mu`minīna syuhụd'),
(5916, 85, 8, 'وَمَا نَقَمُوْا مِنْهُمْ اِلَّآ اَنْ يُّؤْمِنُوْا بِاللّٰهِ الْعَزِيْزِ الْحَمِيْدِۙ', 'Dan mereka menyiksa orang-orang mukmin itu hanya karena (orang-orang mukmin itu) beriman kepada Allah Yang Mahaperkasa, Maha Terpuji,', 'wa mā naqamụ min-hum illā ay yu`minụ billāhil-\'azīzil-ḥamīd'),
(5917, 85, 9, 'الَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ۗ', 'yang memiliki kerajaan langit dan bumi. Dan Allah Maha Menyaksikan segala sesuatu.', 'allażī lahụ mulkus-samāwāti wal-arḍ, wallāhu \'alā kulli syai`in syahīd'),
(5918, 85, 10, 'اِنَّ الَّذِيْنَ فَتَنُوا الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ ثُمَّ لَمْ يَتُوْبُوْا فَلَهُمْ عَذَابُ جَهَنَّمَ وَلَهُمْ عَذَابُ الْحَرِيْقِۗ', 'Sungguh, orang-orang yang mendatangkan cobaan (bencana, membunuh, menyiksa) kepada orang-orang mukmin laki-laki dan perempuan lalu mereka tidak bertobat, maka mereka akan mendapat azab Jahanam dan mereka akan mendapat azab (neraka) yang membakar.', 'innallażīna fatanul-mu`minīna wal-mu`mināti ṡumma lam yatụbụ fa lahum \'ażābu jahannama wa lahum \'ażābul-ḥarīq'),
(5919, 85, 11, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ەۗ ذٰلِكَ الْفَوْزُ الْكَبِيْرُۗ', 'Sungguh, orang-orang yang beriman dan mengerjakan kebajikan, mereka akan mendapat surga yang mengalir di bawahnya sungai-sungai, itulah kemenangan yang agung.', 'innallażīna āmanụ wa \'amiluṣ-ṣāliḥāti lahum jannātun tajrī min taḥtihal-an-hār, żālikal-fauzul-kabīr'),
(5920, 85, 12, 'اِنَّ بَطْشَ رَبِّكَ لَشَدِيْدٌ ۗ', 'Sungguh, azab Tuhanmu sangat keras.', 'inna baṭsya rabbika lasyadīd'),
(5921, 85, 13, 'اِنَّهٗ هُوَ يُبْدِئُ وَيُعِيْدُۚ', 'Sungguh, Dialah yang memulai pen-ciptaan (makhluk) dan yang menghidupkannya (kembali).', 'innahụ huwa yubdi`u wa yu\'īd'),
(5922, 85, 14, 'وَهُوَ الْغَفُوْرُ الْوَدُوْدُۙ', 'Dan Dialah Yang Maha Pengampun, Maha Pengasih,', 'wa huwal-gafụrul-wadụd'),
(5923, 85, 15, 'ذُو الْعَرْشِ الْمَجِيْدُۙ', 'yang memiliki ‘Arsy, lagi Mahamulia,', 'żul-\'arsyil-majīd'),
(5924, 85, 16, 'فَعَّالٌ لِّمَا يُرِيْدُۗ', 'Mahakuasa berbuat apa yang Dia kehendaki.', 'fa\'\'ālul limā yurīd'),
(5925, 85, 17, 'هَلْ اَتٰىكَ حَدِيْثُ الْجُنُوْدِۙ', 'Sudahkah sampai kepadamu berita tentang bala tentara (penentang),', 'hal atāka ḥadīṡul-junụd'),
(5926, 85, 18, 'فِرْعَوْنَ وَثَمُوْدَۗ', '(yaitu) Fir‘aun dan Samud?', 'fir\'auna wa ṡamụd'),
(5927, 85, 19, 'بَلِ الَّذِيْنَ كَفَرُوْا فِيْ تَكْذِيْبٍۙ', 'Memang orang-orang kafir (selalu) mendustakan,', 'balillażīna kafarụ fī takżīb'),
(5928, 85, 20, 'وَّاللّٰهُ مِنْ وَّرَاۤىِٕهِمْ مُّحِيْطٌۚ', 'padahal Allah mengepung dari belakang mereka (sehingga tidak dapat lolos).', 'wallāhu miw warā`ihim muḥīṭ'),
(5929, 85, 21, 'بَلْ هُوَ قُرْاٰنٌ مَّجِيْدٌۙ', 'Bahkan (yang didustakan itu) ialah Al-Qur\'an yang mulia,', 'bal huwa qur`ānum majīd'),
(5930, 85, 22, 'فِيْ لَوْحٍ مَّحْفُوْظٍ', 'yang (tersimpan) dalam (tempat) yang terjaga (Lauh Mahfuzh).', 'fī lauḥim maḥfụẓ'),
(5931, 86, 1, 'وَالسَّمَاۤءِ وَالطَّارِقِۙ', 'Demi langit dan yang datang pada malam hari.', 'was-samā`i waṭ-ṭāriq'),
(5932, 86, 2, 'وَمَآ اَدْرٰىكَ مَا الطَّارِقُۙ', 'Dan tahukah kamu apakah yang datang pada malam hari itu?', 'wa mā adrāka maṭ-ṭāriq'),
(5933, 86, 3, 'النَّجْمُ الثَّاقِبُۙ', '(yaitu) bintang yang bersinar tajam,', 'an-najmuṡ-ṡāqib'),
(5934, 86, 4, 'اِنْ كُلُّ نَفْسٍ لَّمَّا عَلَيْهَا حَافِظٌۗ', 'setiap orang pasti ada penjaganya.', 'ing kullu nafsil lammā \'alaihā ḥāfiẓ'),
(5935, 86, 5, 'فَلْيَنْظُرِ الْاِنْسَانُ مِمَّ خُلِقَ', 'Maka hendaklah manusia memperhatikan dari apa dia diciptakan.', 'falyanẓuril-insānu mimma khuliq'),
(5936, 86, 6, 'خُلِقَ مِنْ مَّاۤءٍ دَافِقٍۙ', 'Dia diciptakan dari air (mani) yang terpancar,', 'khuliqa mim mā`in dāfiq'),
(5937, 86, 7, 'يَّخْرُجُ مِنْۢ بَيْنِ الصُّلْبِ وَالتَّرَاۤىِٕبِۗ', 'yang keluar dari antara tulang punggung (sulbi) dan tulang dada.', 'yakhruju mim bainiṣ-ṣulbi wat-tarā`ib'),
(5938, 86, 8, 'اِنَّهٗ عَلٰى رَجْعِهٖ لَقَادِرٌۗ', 'Sungguh, Allah benar-benar kuasa untuk mengembalikannya (hidup setelah mati).', 'innahụ \'alā raj\'ihī laqādir'),
(5939, 86, 9, 'يَوْمَ تُبْلَى السَّرَاۤىِٕرُۙ', 'Pada hari ditampakkan segala rahasia,', 'yauma tublas-sarā`ir'),
(5940, 86, 10, 'فَمَا لَهٗ مِنْ قُوَّةٍ وَّلَا نَاصِرٍۗ', 'maka manusia tidak lagi mempunyai suatu kekuatan dan tidak (pula) ada penolong.', 'fa mā lahụ ming quwwatiw wa lā nāṣir'),
(5941, 86, 11, 'وَالسَّمَاۤءِ ذَاتِ الرَّجْعِۙ', 'Demi langit yang mengandung hujan,', 'was-samā`i żātir-raj\''),
(5942, 86, 12, 'وَالْاَرْضِ ذَاتِ الصَّدْعِۙ', 'dan bumi yang mempunyai tumbuh-tumbuhan,', 'wal-arḍi żātiṣ-ṣad\''),
(5943, 86, 13, 'اِنَّهٗ لَقَوْلٌ فَصْلٌۙ', 'sungguh, (Al-Qur\'an) itu benar-benar firman pemisah (antara yang hak dan yang batil),', 'innahụ laqaulun faṣl'),
(5944, 86, 14, 'وَّمَا هُوَ بِالْهَزْلِۗ', 'dan (Al-Qur\'an) itu bukanlah sendagurauan.', 'wa mā huwa bil-hazl'),
(5945, 86, 15, 'اِنَّهُمْ يَكِيْدُوْنَ كَيْدًاۙ', 'Sungguh, mereka (orang kafir) merencanakan tipu daya yang jahat.', 'innahum yakīdụna kaidā'),
(5946, 86, 16, 'وَّاَكِيْدُ كَيْدًاۖ', 'Dan Aku pun membuat rencana (tipu daya) yang jitu.', 'wa akīdu kaidā'),
(5947, 86, 17, 'فَمَهِّلِ الْكٰفِرِيْنَ اَمْهِلْهُمْ رُوَيْدًا', 'Karena itu berilah penangguhan kepada orang-orang kafir itu. Berilah mereka itu kesempatan untuk sementara waktu.', 'fa mahhilil-kāfirīna am-hil-hum ruwaidā'),
(5948, 87, 1, 'سَبِّحِ اسْمَ رَبِّكَ الْاَعْلَىۙ', 'Sucikanlah nama Tuhanmu Yang Mahatinggi,', 'sabbiḥisma rabbikal-a\'lā'),
(5949, 87, 2, 'الَّذِيْ خَلَقَ فَسَوّٰىۖ', 'Yang menciptakan, lalu menyempurnakan (ciptaan-Nya).', 'allażī khalaqa fa sawwā'),
(5950, 87, 3, 'وَالَّذِيْ قَدَّرَ فَهَدٰىۖ', 'Yang menentukan kadar (masing-masing) dan memberi petunjuk,', 'wallażī qaddara fa hadā'),
(5951, 87, 4, 'وَالَّذِيْٓ اَخْرَجَ الْمَرْعٰىۖ', 'dan Yang menumbuhkan rerumputan,', 'wallażī akhrajal-mar\'ā'),
(5952, 87, 5, 'فَجَعَلَهٗ غُثَاۤءً اَحْوٰىۖ', 'lalu dijadikan-Nya (rumput-rumput) itu kering kehitam-hitaman.', 'fa ja\'alahụ guṡā`an aḥwā'),
(5953, 87, 6, 'سَنُقْرِئُكَ فَلَا تَنْسٰىٓ ۖ', 'Kami akan membacakan (Al-Qur\'an) kepadamu (Muhammad) sehingga engkau tidak akan lupa,', 'sanuqri`uka fa lā tansā'),
(5954, 87, 7, 'اِلَّا مَا شَاۤءَ اللّٰهُ ۗاِنَّهٗ يَعْلَمُ الْجَهْرَ وَمَا يَخْفٰىۗ', 'kecuali jika Allah menghendaki. Sungguh, Dia mengetahui yang terang dan yang tersembunyi.', 'illā mā syā`allāh, innahụ ya\'lamul-jahra wa mā yakhfā'),
(5955, 87, 8, 'وَنُيَسِّرُكَ لِلْيُسْرٰىۖ', 'Dan Kami akan memudahkan bagimu ke jalan kemudahan (mencapai kebahagiaan dunia dan akhirat),', 'wa nuyassiruka lil-yusrā'),
(5956, 87, 9, 'فَذَكِّرْ اِنْ نَّفَعَتِ الذِّكْرٰىۗ', 'oleh sebab itu berikanlah peringatan, karena peringatan itu bermanfaat,', 'fa żakkir in nafa\'atiż-żikrā'),
(5957, 87, 10, 'سَيَذَّكَّرُ مَنْ يَّخْشٰىۙ', 'orang yang takut (kepada Allah) akan mendapat pelajaran,', 'sayażżakkaru may yakhsyā'),
(5958, 87, 11, 'وَيَتَجَنَّبُهَا الْاَشْقَىۙ', 'dan orang-orang yang celaka (kafir) akan menjauhinya,', 'wa yatajannabuhal-asyqā'),
(5959, 87, 12, 'الَّذِيْ يَصْلَى النَّارَ الْكُبْرٰىۚ', '(yaitu) orang yang akan memasuki api yang besar (neraka),', 'allażī yaṣlan-nāral-kubrā'),
(5960, 87, 13, 'ثُمَّ لَا يَمُوْتُ فِيْهَا وَلَا يَحْيٰىۗ', 'selanjutnya dia di sana tidak mati dan tidak (pula) hidup.', 'ṡumma lā yamụtu fīhā wa lā yaḥyā'),
(5961, 87, 14, 'قَدْ اَفْلَحَ مَنْ تَزَكّٰىۙ', 'Sungguh beruntung orang yang menyucikan diri (dengan beriman),', 'qad aflaḥa man tazakkā'),
(5962, 87, 15, 'وَذَكَرَ اسْمَ رَبِّهٖ فَصَلّٰىۗ', 'dan mengingat nama Tuhannya, lalu dia salat.', 'wa żakarasma rabbihī fa ṣallā'),
(5963, 87, 16, 'بَلْ تُؤْثِرُوْنَ الْحَيٰوةَ الدُّنْيَاۖ', 'Sedangkan kamu (orang-orang kafir) memilih kehidupan dunia,', 'bal tu`ṡirụnal-ḥayātad-dun-yā'),
(5964, 87, 17, 'وَالْاٰخِرَةُ خَيْرٌ وَّاَبْقٰىۗ', 'padahal kehidupan akhirat itu lebih baik dan lebih kekal.', 'wal-ākhiratu khairuw wa abqā'),
(5965, 87, 18, 'اِنَّ هٰذَا لَفِى الصُّحُفِ الْاُوْلٰىۙ', 'Sesungguhnya ini terdapat dalam kitab-kitab yang dahulu,', 'inna hāżā lafiṣ-ṣuḥufil-ụlā'),
(5966, 87, 19, 'صُحُفِ اِبْرٰهِيْمَ وَمُوْسٰى', '(yaitu) kitab-kitab Ibrahim dan Musa.', 'ṣuḥufi ibrāhīma wa mụsā'),
(5967, 88, 1, 'هَلْ اَتٰىكَ حَدِيْثُ الْغَاشِيَةِۗ', 'Sudahkah sampai kepadamu berita tentang (hari Kiamat)?', 'hal atāka ḥadīṡul-gāsyiyah'),
(5968, 88, 2, 'وُجُوْهٌ يَّوْمَىِٕذٍ خَاشِعَةٌ ۙ', 'Pada hari itu banyak wajah yang tertunduk terhina,', 'wujụhuy yauma`iżin khāsyi\'ah'),
(5969, 88, 3, 'عَامِلَةٌ نَّاصِبَةٌ ۙ', '(karena) bekerja keras lagi kepayahan,', '\'āmilatun nāṣibah'),
(5970, 88, 4, 'تَصْلٰى نَارًا حَامِيَةً ۙ', 'mereka memasuki api yang sangat panas (neraka),', 'taṣlā nāran ḥāmiyah'),
(5971, 88, 5, 'تُسْقٰى مِنْ عَيْنٍ اٰنِيَةٍ ۗ', 'diberi minum dari sumber mata air yang sangat panas.', 'tusqā min \'ainin āniyah'),
(5972, 88, 6, 'لَيْسَ لَهُمْ طَعَامٌ اِلَّا مِنْ ضَرِيْعٍۙ', 'Tidak ada makanan bagi mereka selain dari pohon yang berduri,', 'laisa lahum ṭa\'āmun illā min ḍarī\''),
(5973, 88, 7, 'لَّا يُسْمِنُ وَلَا يُغْنِيْ مِنْ جُوْعٍۗ', 'yang tidak menggemukkan dan tidak menghilangkan lapar.', 'lā yusminu wa lā yugnī min jụ\''),
(5974, 88, 8, 'وُجُوْهٌ يَّوْمَىِٕذٍ نَّاعِمَةٌ ۙ', 'Pada hari itu banyak (pula) wajah yang berseri-seri,', 'wujụhuy yauma`iżin nā\'imah'),
(5975, 88, 9, 'لِّسَعْيِهَا رَاضِيَةٌ ۙ', 'merasa senang karena usahanya (sendiri),', 'lisa\'yihā rāḍiyah'),
(5976, 88, 10, 'فِيْ جَنَّةٍ عَالِيَةٍۙ', '(mereka) dalam surga yang tinggi,', 'fī jannatin \'āliyah'),
(5977, 88, 11, 'لَّا تَسْمَعُ فِيْهَا لَاغِيَةً ۗ', 'di sana (kamu) tidak mendengar perkataan yang tidak berguna.', 'lā tasma\'u fīhā lāgiyah'),
(5978, 88, 12, 'فِيْهَا عَيْنٌ جَارِيَةٌ ۘ', 'Di sana ada mata air yang mengalir.', 'fīhā \'ainun jāriyah'),
(5979, 88, 13, 'فِيْهَا سُرُرٌ مَّرْفُوْعَةٌ ۙ', 'Di sana ada dipan-dipan yang ditinggikan,', 'fīhā sururum marfụ\'ah'),
(5980, 88, 14, 'وَّاَكْوَابٌ مَّوْضُوْعَةٌ ۙ', 'dan gelas-gelas yang tersedia (di dekatnya),', 'wa akwābum mauḍụ\'ah'),
(5981, 88, 15, 'وَّنَمَارِقُ مَصْفُوْفَةٌ ۙ', 'dan bantal-bantal sandaran yang tersusun,', 'wa namāriqu maṣfụfah'),
(5982, 88, 16, 'وَّزَرَابِيُّ مَبْثُوْثَةٌ ۗ', 'dan permadani-permadani yang terhampar.', 'wa zarābiyyu mabṡụṡah'),
(5983, 88, 17, 'اَفَلَا يَنْظُرُوْنَ اِلَى الْاِبِلِ كَيْفَ خُلِقَتْۗ', 'Maka tidakkah mereka memperhatikan unta, bagaimana diciptakan?', 'a fa lā yanẓurụna ilal-ibili kaifa khuliqat'),
(5984, 88, 18, 'وَاِلَى السَّمَاۤءِ كَيْفَ رُفِعَتْۗ', 'dan langit, bagaimana ditinggikan?', 'wa ilas-samā`i kaifa rufi\'at'),
(5985, 88, 19, 'وَاِلَى الْجِبَالِ كَيْفَ نُصِبَتْۗ', 'Dan gunung-gunung bagaimana ditegakkan?', 'wa ilal-jibāli kaifa nuṣibat'),
(5986, 88, 20, 'وَاِلَى الْاَرْضِ كَيْفَ سُطِحَتْۗ', 'Dan bumi bagaimana dihamparkan?', 'wa ilal-arḍi kaifa suṭiḥat'),
(5987, 88, 21, 'فَذَكِّرْۗ اِنَّمَآ اَنْتَ مُذَكِّرٌۙ', 'Maka berilah peringatan, karena sesungguhnya engkau (Muhammad) hanyalah pemberi peringatan.', 'fa żakkir, innamā anta mużakkir'),
(5988, 88, 22, 'لَّسْتَ عَلَيْهِمْ بِمُصَيْطِرٍۙ', 'Engkau bukanlah orang yang berkuasa atas mereka,', 'lasta \'alaihim bimuṣaiṭir'),
(5989, 88, 23, 'اِلَّا مَنْ تَوَلّٰى وَكَفَرَۙ', 'kecuali (jika ada) orang yang berpaling dan kafir,', 'illā man tawallā wa kafar'),
(5990, 88, 24, 'فَيُعَذِّبُهُ اللّٰهُ الْعَذَابَ الْاَكْبَرَۗ', 'maka Allah akan mengazabnya dengan azab yang besar.', 'fa yu\'ażżibuhullāhul-\'ażābal-akbar'),
(5991, 88, 25, 'اِنَّ اِلَيْنَآ اِيَابَهُمْ', 'Sungguh, kepada Kamilah mereka kembali,', 'inna ilainā iyābahum'),
(5992, 88, 26, 'ثُمَّ اِنَّ عَلَيْنَا حِسَابَهُمْ', 'kemudian sesungguhnya (kewajiban) Kamilah membuat perhitungan atas mereka.', 'ṡumma inna \'alainā ḥisābahum'),
(5993, 89, 1, 'وَالْفَجْرِۙ', 'Demi fajar,', 'wal-fajr'),
(5994, 89, 2, 'وَلَيَالٍ عَشْرٍۙ', 'demi malam yang sepuluh,', 'wa layālin \'asyr'),
(5995, 89, 3, 'وَّالشَّفْعِ وَالْوَتْرِۙ', 'demi yang genap dan yang ganjil,', 'wasy-syaf\'i wal-watr'),
(5996, 89, 4, 'وَالَّيْلِ اِذَا يَسْرِۚ', 'demi malam apabila berlalu.', 'wal-laili iżā yasr'),
(5997, 89, 5, 'هَلْ فِيْ ذٰلِكَ قَسَمٌ لِّذِيْ حِجْرٍۗ', 'Adakah pada yang demikian itu terdapat sumpah (yang dapat diterima) bagi orang-orang yang berakal?', 'hal fī żālika qasamul liżī ḥijr'),
(5998, 89, 6, 'اَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِعَادٍۖ', 'Tidakkah engkau (Muhammad) memperhatikan bagaimana Tuhanmu berbuat terhadap (kaum) ‘Ad?', 'a lam tara kaifa fa\'ala rabbuka bi\'ād');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `indoText`, `readText`) VALUES
(5999, 89, 7, 'اِرَمَ ذَاتِ الْعِمَادِۖ', '(yaitu) penduduk Iram (ibukota kaum ‘Ad) yang mempunyai bangunan-bangunan yang tinggi,', 'irama żātil-\'imād'),
(6000, 89, 8, 'الَّتِيْ لَمْ يُخْلَقْ مِثْلُهَا فِى الْبِلَادِۖ', 'yang belum pernah dibangun (suatu kota) seperti itu di negeri-negeri lain,', 'allatī lam yukhlaq miṡluhā fil-bilād'),
(6001, 89, 9, 'وَثَمُوْدَ الَّذِيْنَ جَابُوا الصَّخْرَ بِالْوَادِۖ', 'dan (terhadap) kaum samud yang memotong batu-batu besar di lembah,', 'wa ṡamụdallażīna jābuṣ-ṣakhra bil-wād'),
(6002, 89, 10, 'وَفِرْعَوْنَ ذِى الْاَوْتَادِۖ', 'dan (terhadap) Fir‘aun yang mempunyai pasak-pasak (bangunan yang besar),', 'wa fir\'auna żil-autād'),
(6003, 89, 11, 'الَّذِيْنَ طَغَوْا فِى الْبِلَادِۖ', 'yang berbuat sewenang-wenang dalam negeri,', 'allażīna ṭagau fil-bilād'),
(6004, 89, 12, 'فَاَكْثَرُوْا فِيْهَا الْفَسَادَۖ', 'lalu mereka banyak berbuat kerusakan dalam negeri itu,', 'fa akṡarụ fīhal-fasād'),
(6005, 89, 13, 'فَصَبَّ عَلَيْهِمْ رَبُّكَ سَوْطَ عَذَابٍۖ', 'karena itu Tuhanmu menimpakan cemeti azab kepada mereka,', 'fa ṣabba \'alaihim rabbuka sauṭa \'ażāb'),
(6006, 89, 14, 'اِنَّ رَبَّكَ لَبِالْمِرْصَادِۗ', 'sungguh, Tuhanmu benar-benar mengawasi.', 'inna rabbaka labil-mirṣād'),
(6007, 89, 15, 'فَاَمَّا الْاِنْسَانُ اِذَا مَا ابْتَلٰىهُ رَبُّهٗ فَاَكْرَمَهٗ وَنَعَّمَهٗۙ فَيَقُوْلُ رَبِّيْٓ اَكْرَمَنِۗ', 'Maka adapun manusia, apabila Tuhan mengujinya lalu memuliakannya dan memberinya kesenangan, maka dia berkata, “Tuhanku telah memuliakanku.”', 'fa ammal-insānu iżā mabtalāhu rabbuhụ fa akramahụ wa na\'\'amahụ fa yaqụlu rabbī akraman'),
(6008, 89, 16, 'وَاَمَّآ اِذَا مَا ابْتَلٰىهُ فَقَدَرَ عَلَيْهِ رِزْقَهٗ ەۙ فَيَقُوْلُ رَبِّيْٓ اَهَانَنِۚ', 'Namun apabila Tuhan mengujinya lalu membatasi rezekinya, maka dia berkata, “Tuhanku telah menghinaku.”', 'wa ammā iżā mabtalāhu fa qadara \'alaihi rizqahụ fa yaqụlu rabbī ahānan'),
(6009, 89, 17, 'كَلَّا بَلْ لَّا تُكْرِمُوْنَ الْيَتِيْمَۙ', 'Sekali-kali tidak! Bahkan kamu tidak memuliakan anak yatim,', 'kallā bal lā tukrimụnal-yatīm'),
(6010, 89, 18, 'وَلَا تَحٰۤضُّوْنَ عَلٰى طَعَامِ الْمِسْكِيْنِۙ', 'dan kamu tidak saling mengajak memberi makan orang miskin,', 'wa lā tahāḍḍụna \'alā ṭa\'āmil-miskīn'),
(6011, 89, 19, 'وَتَأْكُلُوْنَ التُّرَاثَ اَكْلًا لَّمًّاۙ', 'sedangkan kamu memakan harta warisan dengan cara mencampurbaurkan (yang halal dan yang haram),', 'wa ta`kulụnat-turāṡa aklal lammā'),
(6012, 89, 20, 'وَّتُحِبُّوْنَ الْمَالَ حُبًّا جَمًّاۗ', 'dan kamu mencintai harta dengan kecintaan yang berlebihan.', 'wa tuḥibbụnal-māla ḥubban jammā'),
(6013, 89, 21, 'كَلَّآ اِذَا دُكَّتِ الْاَرْضُ دَكًّا دَكًّاۙ', 'Sekali-kali tidak! Apabila bumi diguncangkan berturut-turut (berbenturan),', 'kallā iżā dukkatil-arḍu dakkan dakkā'),
(6014, 89, 22, 'وَّجَآءَ رَبُّكَ وَالْمَلَكُ صَفًّا صَفًّاۚ', 'dan datanglah Tuhanmu; dan malaikat berbaris-baris,', 'wa jā`a rabbuka wal-malaku ṣaffan ṣaffā'),
(6015, 89, 23, 'وَجِايْۤءَ يَوْمَىِٕذٍۢ بِجَهَنَّمَۙ يَوْمَىِٕذٍ يَّتَذَكَّرُ الْاِنْسَانُ وَاَنّٰى لَهُ الذِّكْرٰىۗ', 'dan pada hari itu diperlihatkan neraka Jahanam; pada hari itu sadarlah manusia, tetapi tidak berguna lagi baginya kesadaran itu.', 'wa jī`a yauma`iżim bijahannama yauma`iżiy yatażakkarul-insānu wa annā lahuż-żikrā'),
(6016, 89, 24, 'يَقُوْلُ يٰلَيْتَنِيْ قَدَّمْتُ لِحَيَاتِيْۚ', 'Dia berkata, “Alangkah baiknya sekiranya dahulu aku mengerjakan (kebajikan) untuk hidupku ini.”', 'yaqụlu yā laitanī qaddamtu liḥayātī'),
(6017, 89, 25, 'فَيَوْمَىِٕذٍ لَّا يُعَذِّبُ عَذَابَهٗٓ اَحَدٌ ۙ', 'Maka pada hari itu tidak ada seorang pun yang mengazab seperti azab-Nya (yang adil),', 'fa yauma`iżil lā yu\'ażżibu \'ażābahū aḥad'),
(6018, 89, 26, 'وَّلَا يُوْثِقُ وَثَاقَهٗٓ اَحَدٌ ۗ', 'dan tidak ada seorang pun yang mengikat seperti ikatan-Nya.', 'wa lā yụṡiqu waṡāqahū aḥad'),
(6019, 89, 27, 'يٰٓاَيَّتُهَا النَّفْسُ الْمُطْمَىِٕنَّةُۙ', 'Wahai jiwa yang tenang!', 'yā ayyatuhan-nafsul-muṭma`innah'),
(6020, 89, 28, 'ارْجِعِيْٓ اِلٰى رَبِّكِ رَاضِيَةً مَّرْضِيَّةً ۚ', 'Kembalilah kepada Tuhanmu dengan hati yang rida dan diridai-Nya.', 'irji\'ī ilā rabbiki rāḍiyatam marḍiyyah'),
(6021, 89, 29, 'فَادْخُلِيْ فِيْ عِبٰدِيْۙ', 'Maka masuklah ke dalam golongan hamba-hamba-Ku,', 'fadkhulī fī \'ibādī'),
(6022, 89, 30, 'وَادْخُلِيْ جَنَّتِيْࣖ', 'dan masuklah ke dalam surga-Ku.', 'wadkhulī jannatī'),
(6023, 90, 1, 'لَآ اُقْسِمُ بِهٰذَا الْبَلَدِۙ', 'Aku bersumpah dengan negeri ini (Mekah),', 'lā uqsimu bihāżal-balad'),
(6024, 90, 2, 'وَاَنْتَ حِلٌّۢ بِهٰذَا الْبَلَدِۙ', 'dan engkau (Muhammad), bertempat di negeri (Mekah) ini,', 'wa anta ḥillum bihāżal-balad'),
(6025, 90, 3, 'وَوَالِدٍ وَّمَا وَلَدَۙ', 'dan demi (pertalian) bapak dan anaknya.', 'wa wālidiw wa mā walad'),
(6026, 90, 4, 'لَقَدْ خَلَقْنَا الْاِنْسَانَ فِيْ كَبَدٍۗ', 'Sungguh, Kami telah menciptakan manusia berada dalam susah payah.', 'laqad khalaqnal-insāna fī kabad'),
(6027, 90, 5, 'اَيَحْسَبُ اَنْ لَّنْ يَّقْدِرَ عَلَيْهِ اَحَدٌ ۘ', 'Apakah dia (manusia) itu mengira bahwa tidak ada sesuatu pun yang berkuasa atasnya?', 'a yaḥsabu al lay yaqdira \'alaihi aḥad'),
(6028, 90, 6, 'يَقُوْلُ اَهْلَكْتُ مَالًا لُّبَدًاۗ', 'Dia mengatakan, “Aku telah menghabiskan harta yang banyak.”', 'yaqụlu ahlaktu mālal lubadā'),
(6029, 90, 7, 'اَيَحْسَبُ اَنْ لَّمْ يَرَهٗٓ اَحَدٌۗ', 'Apakah dia mengira bahwa tidak ada sesuatu pun yang melihatnya?', 'a yaḥsabu al lam yarahū aḥad'),
(6030, 90, 8, 'اَلَمْ نَجْعَلْ لَّهٗ عَيْنَيْنِۙ', 'Bukankah Kami telah menjadikan untuknya sepasang mata,', 'a lam naj\'al lahụ \'ainaīn'),
(6031, 90, 9, 'وَلِسَانًا وَّشَفَتَيْنِۙ', 'dan lidah dan sepasang bibir?', 'wa lisānaw wa syafataīn'),
(6032, 90, 10, 'وَهَدَيْنٰهُ النَّجْدَيْنِۙ', 'Dan Kami telah menunjukkan kepadanya dua jalan (kebajikan dan kejahatan),', 'wa hadaināhun-najdaīn'),
(6033, 90, 11, 'فَلَا اقْتَحَمَ الْعَقَبَةَ ۖ', 'tetapi dia tidak menempuh jalan yang mendaki dan sukar?', 'fa laqtaḥamal-\'aqabah'),
(6034, 90, 12, 'وَمَآ اَدْرٰىكَ مَا الْعَقَبَةُ ۗ', 'Dan tahukah kamu apakah jalan yang mendaki dan sukar itu?', 'wa mā adrāka mal-\'aqabah'),
(6035, 90, 13, 'فَكُّ رَقَبَةٍۙ', '(yaitu) melepaskan perbudakan (hamba sahaya),', 'fakku raqabah'),
(6036, 90, 14, 'اَوْ اِطْعَامٌ فِيْ يَوْمٍ ذِيْ مَسْغَبَةٍۙ', 'atau memberi makan pada hari terjadi kelaparan,', 'au iṭ\'āmun fī yaumin żī masgabah'),
(6037, 90, 15, 'يَّتِيْمًا ذَا مَقْرَبَةٍۙ', '(kepada) anak yatim yang ada hubungan kerabat,', 'yatīman żā maqrabah'),
(6038, 90, 16, 'اَوْ مِسْكِيْنًا ذَا مَتْرَبَةٍۗ', 'atau orang miskin yang sangat fakir.', 'au miskīnan żā matrabah'),
(6039, 90, 17, 'ثُمَّ كَانَ مِنَ الَّذِيْنَ اٰمَنُوْا وَتَوَاصَوْا بِالصَّبْرِ وَتَوَاصَوْا بِالْمَرْحَمَةِۗ', 'Kemudian dia termasuk orang-orang yang beriman dan saling berpesan untuk bersabar dan saling berpesan untuk berkasih sayang.', 'ṡumma kāna minallażīna āmanụ wa tawāṣau biṣ-ṣabri wa tawāṣau bil-mar-ḥamah'),
(6040, 90, 18, 'اُولٰۤىِٕكَ اَصْحٰبُ الْمَيْمَنَةِۗ', 'Mereka (orang-orang yang beriman dan saling berpesan itu) adalah golongan kanan.', 'ulā`ika aṣ-ḥābul-maimanah'),
(6041, 90, 19, 'وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِنَا هُمْ اَصْحٰبُ الْمَشْئَمَةِۗ', 'Dan orang-orang yang kafir kepada ayat-ayat Kami, mereka itu adalah golongan kiri.', 'wallażīna kafarụ bi`āyātinā hum aṣ-ḥābul-masy`amah'),
(6042, 90, 20, 'عَلَيْهِمْ نَارٌ مُّؤْصَدَةٌ', 'Mereka berada dalam neraka yang ditutup rapat.', '\'alaihim nārum mu`ṣadah'),
(6043, 91, 1, 'وَالشَّمْسِ وَضُحٰىهَاۖ', 'Demi matahari dan sinarnya pada pagi hari,', 'wasy-syamsi wa ḍuḥāhā'),
(6044, 91, 2, 'وَالْقَمَرِ اِذَا تَلٰىهَاۖ', 'demi bulan apabila mengiringinya,', 'wal-qamari iżā talāhā'),
(6045, 91, 3, 'وَالنَّهَارِ اِذَا جَلّٰىهَاۖ', 'demi siang apabila menampakkannya,', 'wan-nahāri iżā jallāhā'),
(6046, 91, 4, 'وَالَّيْلِ اِذَا يَغْشٰىهَاۖ', 'demi malam apabila menutupinya (gelap gulita),', 'wal-laili iżā yagsyāhā'),
(6047, 91, 5, 'وَالسَّمَاۤءِ وَمَا بَنٰىهَاۖ', 'demi langit serta pembinaannya (yang menakjubkan),', 'was-samā`i wa mā banāhā'),
(6048, 91, 6, 'وَالْاَرْضِ وَمَا طَحٰىهَاۖ', 'demi bumi serta penghamparannya,', 'wal-arḍi wa mā ṭaḥāhā'),
(6049, 91, 7, 'وَنَفْسٍ وَّمَا سَوّٰىهَاۖ', 'demi jiwa serta penyempurnaan (ciptaan)nya,', 'wa nafsiw wa mā sawwāhā'),
(6050, 91, 8, 'فَاَلْهَمَهَا فُجُوْرَهَا وَتَقْوٰىهَاۖ', 'maka Dia mengilhamkan kepadanya (jalan) kejahatan dan ketakwaannya,', 'fa al-hamahā fujụrahā wa taqwāhā'),
(6051, 91, 9, 'قَدْ اَفْلَحَ مَنْ زَكّٰىهَاۖ', 'sungguh beruntung orang yang menyucikannya (jiwa itu),', 'qad aflaḥa man zakkāhā'),
(6052, 91, 10, 'وَقَدْ خَابَ مَنْ دَسّٰىهَاۗ', 'dan sungguh rugi orang yang mengotorinya.', 'wa qad khāba man dassāhā'),
(6053, 91, 11, 'كَذَّبَتْ ثَمُوْدُ بِطَغْوٰىهَآ ۖ', '(Kaum) samud telah mendustakan (rasulnya) karena mereka melampaui batas (zalim),', 'każżabaṡ ṡamụdu biṭagwāhā'),
(6054, 91, 12, 'اِذِ انْۢبَعَثَ اَشْقٰىهَاۖ', 'ketika bangkit orang yang paling celaka di antara mereka,', 'iżimba\'aṡa asyqāhā'),
(6055, 91, 13, 'فَقَالَ لَهُمْ رَسُوْلُ اللّٰهِ نَاقَةَ اللّٰهِ وَسُقْيٰهَاۗ', 'lalu Rasul Allah (Saleh) berkata kepada mereka, “(Biarkanlah) unta betina dari Allah ini dengan minumannya.”', 'fa qāla lahum rasụlullāhi nāqatallāhi wa suqyāhā'),
(6056, 91, 14, 'فَكَذَّبُوْهُ فَعَقَرُوْهَاۖ فَدَمْدَمَ عَلَيْهِمْ رَبُّهُمْ بِذَنْۢبِهِمْ فَسَوّٰىهَاۖ', 'Namun mereka mendustakannya dan menyembelihnya, karena itu Tuhan membinasakan mereka karena dosanya, lalu diratakan-Nya (dengan tanah),', 'fa każżabụhu fa \'aqarụhā fa damdama \'alaihim rabbuhum biżambihim fa sawwāhā'),
(6057, 91, 15, 'وَلَا يَخَافُ عُقْبٰهَا', 'dan Dia tidak takut terhadap akibatnya.', 'wa lā yakhāfu \'uqbāhā'),
(6058, 92, 1, 'وَالَّيْلِ اِذَا يَغْشٰىۙ', 'Demi malam apabila menutupi (cahaya siang),', 'wal-laili iżā yagsyā'),
(6059, 92, 2, 'وَالنَّهَارِ اِذَا تَجَلّٰىۙ', 'demi siang apabila terang benderang,', 'wan-nahāri iżā tajallā'),
(6060, 92, 3, 'وَمَا خَلَقَ الذَّكَرَ وَالْاُنْثٰىٓ ۙ', 'demi penciptaan laki-laki dan perempuan,', 'wa mā khalaqaż-żakara wal-unṡā'),
(6061, 92, 4, 'اِنَّ سَعْيَكُمْ لَشَتّٰىۗ', 'sungguh, usahamu memang beraneka macam.', 'inna sa\'yakum lasyattā'),
(6062, 92, 5, 'فَاَمَّا مَنْ اَعْطٰى وَاتَّقٰىۙ', 'Maka barangsiapa memberikan (hartanya di jalan Allah) dan bertakwa,', 'fa ammā man a\'ṭā wattaqā'),
(6063, 92, 6, 'وَصَدَّقَ بِالْحُسْنٰىۙ', 'dan membenarkan (adanya pahala) yang terbaik (surga),', 'wa ṣaddaqa bil-ḥusnā'),
(6064, 92, 7, 'فَسَنُيَسِّرُهٗ لِلْيُسْرٰىۗ', 'maka akan Kami mudahkan baginya jalan menuju kemudahan (kebahagiaan),', 'fa sanuyassiruhụ lil-yusrā'),
(6065, 92, 8, 'وَاَمَّا مَنْۢ بَخِلَ وَاسْتَغْنٰىۙ', 'dan adapun orang yang kikir dan merasa dirinya cukup (tidak perlu pertolongan Allah),', 'wa ammā mam bakhila wastagnā'),
(6066, 92, 9, 'وَكَذَّبَ بِالْحُسْنٰىۙ', 'serta mendustakan (pahala) yang terbaik,', 'wa każżaba bil-ḥusnā'),
(6067, 92, 10, 'فَسَنُيَسِّرُهٗ لِلْعُسْرٰىۗ', 'maka akan Kami mudahkan baginya jalan menuju kesukaran (kesengsaraan),', 'fa sanuyassiruhụ lil-\'usrā'),
(6068, 92, 11, 'وَمَا يُغْنِيْ عَنْهُ مَالُهٗٓ اِذَا تَرَدّٰىٓۙ', 'dan hartanya tidak bermanfaat baginya apabila dia telah binasa.', 'wa mā yugnī \'an-hu māluhū iżā taraddā'),
(6069, 92, 12, 'اِنَّ عَلَيْنَا لَلْهُدٰىۖ', 'Sesungguhnya Kamilah yang memberi petunjuk,', 'inna \'alainā lal-hudā'),
(6070, 92, 13, 'وَاِنَّ لَنَا لَلْاٰخِرَةَ وَالْاُوْلٰىۗ', 'dan sesungguhnya milik Kamilah akhirat dan dunia itu.', 'wa inna lanā lal-ākhirata wal-ụlā'),
(6071, 92, 14, 'فَاَنْذَرْتُكُمْ نَارًا تَلَظّٰىۚ', 'Maka Aku memperingatkan kamu dengan neraka yang menyala-nyala,', 'fa anżartukum nāran talaẓẓā'),
(6072, 92, 15, 'لَا يَصْلٰىهَآ اِلَّا الْاَشْقَىۙ', 'yang hanya dimasuki oleh orang yang paling celaka,', 'lā yaṣlāhā illal-asyqā'),
(6073, 92, 16, 'الَّذِيْ كَذَّبَ وَتَوَلّٰىۗ', 'yang mendustakan (kebenaran) dan berpaling (dari iman).', 'allażī każżaba wa tawallā'),
(6074, 92, 17, 'وَسَيُجَنَّبُهَا الْاَتْقَىۙ', 'Dan akan dijauhkan darinya (neraka) orang yang paling bertakwa,', 'wa sayujannabuhal-atqā'),
(6075, 92, 18, 'الَّذِيْ يُؤْتِيْ مَالَهٗ يَتَزَكّٰىۚ', 'yang menginfakkan hartanya (di jalan Allah) untuk membersihkan (dirinya),', 'allażī yu`tī mālahụ yatazakkā'),
(6076, 92, 19, 'وَمَا لِاَحَدٍ عِنْدَهٗ مِنْ نِّعْمَةٍ تُجْزٰىٓۙ', 'dan tidak ada seorang pun memberikan suatu nikmat padanya yang harus dibalasnya,', 'wa mā li`aḥadin \'indahụ min ni\'matin tujzā'),
(6077, 92, 20, 'اِلَّا ابْتِغَاۤءَ وَجْهِ رَبِّهِ الْاَعْلٰىۚ', 'tetapi (dia memberikan itu semata-mata) karena mencari keridaan Tuhannya Yang Mahatinggi.', 'illabtigā`a waj-hi rabbihil-a\'lā'),
(6078, 92, 21, 'وَلَسَوْفَ يَرْضٰى', 'Dan niscaya kelak dia akan mendapat kesenangan (yang sempurna).', 'wa lasaufa yarḍā'),
(6079, 93, 1, 'وَالضُّحٰىۙ', 'Demi waktu duha (ketika matahari naik sepenggalah),', 'waḍ-ḍuḥā'),
(6080, 93, 2, 'وَالَّيْلِ اِذَا سَجٰىۙ', 'dan demi malam apabila telah sunyi,', 'wal-laili iżā sajā'),
(6081, 93, 3, 'مَا وَدَّعَكَ رَبُّكَ وَمَا قَلٰىۗ', 'Tuhanmu tidak meninggalkan engkau (Muhammad) dan tidak (pula) membencimu,', 'mā wadda\'aka rabbuka wa mā qalā'),
(6082, 93, 4, 'وَلَلْاٰخِرَةُ خَيْرٌ لَّكَ مِنَ الْاُوْلٰىۗ', 'dan sungguh, yang kemudian itu lebih baik bagimu dari yang permulaan.', 'wa lal-ākhiratu khairul laka minal-ụlā'),
(6083, 93, 5, 'وَلَسَوْفَ يُعْطِيْكَ رَبُّكَ فَتَرْضٰىۗ', 'Dan sungguh, kelak Tuhanmu pasti memberikan karunia-Nya kepadamu, sehingga engkau menjadi puas.', 'wa lasaufa yu\'ṭīka rabbuka fa tarḍā'),
(6084, 93, 6, 'اَلَمْ يَجِدْكَ يَتِيْمًا فَاٰوٰىۖ', 'Bukankah Dia mendapatimu sebagai seorang yatim, lalu Dia melindungi(mu),', 'a lam yajidka yatīman fa āwā'),
(6085, 93, 7, 'وَوَجَدَكَ ضَاۤلًّا فَهَدٰىۖ', 'dan Dia mendapatimu sebagai seorang yang bingung, lalu Dia memberikan petunjuk,', 'wa wajadaka ḍāllan fa hadā'),
(6086, 93, 8, 'وَوَجَدَكَ عَاۤىِٕلًا فَاَغْنٰىۗ', 'dan Dia mendapatimu sebagai seorang yang kekurangan, lalu Dia memberikan kecukupan.', 'wa wajadaka \'ā`ilan fa agnā'),
(6087, 93, 9, 'فَاَمَّا الْيَتِيْمَ فَلَا تَقْهَرْۗ', 'Maka terhadap anak yatim janganlah engkau berlaku sewenang-wenang.', 'fa ammal-yatīma fa lā taq-har'),
(6088, 93, 10, 'وَاَمَّا السَّاۤىِٕلَ فَلَا تَنْهَرْ', 'Dan terhadap orang yang meminta-minta janganlah engkau menghardik(nya).', 'wa ammas-sā`ila fa lā tan-har'),
(6089, 93, 11, 'وَاَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ', 'Dan terhadap nikmat Tuhanmu hendaklah engkau nyatakan (dengan bersyukur).', 'wa ammā bini\'mati rabbika fa ḥaddiṡ'),
(6090, 94, 1, 'اَلَمْ نَشْرَحْ لَكَ صَدْرَكَۙ', 'Bukankah Kami telah melapangkan dadamu (Muhammad)?', 'a lam nasyraḥ laka ṣadrak'),
(6091, 94, 2, 'وَوَضَعْنَا عَنْكَ وِزْرَكَۙ', 'dan Kami pun telah menurunkan bebanmu darimu,', 'wa waḍa\'nā \'angka wizrak'),
(6092, 94, 3, 'الَّذِيْٓ اَنْقَضَ ظَهْرَكَۙ', 'yang memberatkan punggungmu,', 'allażī angqaḍa ẓahrak'),
(6093, 94, 4, 'وَرَفَعْنَا لَكَ ذِكْرَكَۗ', 'dan Kami tinggikan sebutan (nama)mu bagimu.', 'wa rafa\'nā laka żikrak'),
(6094, 94, 5, 'فَاِنَّ مَعَ الْعُسْرِ يُسْرًاۙ', 'Maka sesungguhnya beserta kesulitan ada kemudahan,', 'fa inna ma\'al-\'usri yusrā'),
(6095, 94, 6, 'اِنَّ مَعَ الْعُسْرِ يُسْرًاۗ', 'sesungguhnya beserta kesulitan itu ada kemudahan.', 'inna ma\'al-\'usri yusrā'),
(6096, 94, 7, 'فَاِذَا فَرَغْتَ فَانْصَبْۙ', 'Maka apabila engkau telah selesai (dari sesuatu urusan), tetaplah bekerja keras (untuk urusan yang lain),', 'fa iżā faragta fanṣab'),
(6097, 94, 8, 'وَاِلٰى رَبِّكَ فَارْغَبْ', 'dan hanya kepada Tuhanmulah engkau berharap.', 'wa ilā rabbika fargab'),
(6098, 95, 1, 'وَالتِّيْنِ وَالزَّيْتُوْنِۙ', 'Demi (buah) Tin dan (buah) Zaitun,', 'wat-tīni waz-zaitụn'),
(6099, 95, 2, 'وَطُوْرِ سِيْنِيْنَۙ', 'demi gunung Sinai,', 'wa ṭụri sīnīn'),
(6100, 95, 3, 'وَهٰذَا الْبَلَدِ الْاَمِيْنِۙ', 'dan demi negeri (Mekah) yang aman ini.', 'wa hāżal-baladil-amīn'),
(6101, 95, 4, 'لَقَدْ خَلَقْنَا الْاِنْسَانَ فِيْٓ اَحْسَنِ تَقْوِيْمٍۖ', 'Sungguh, Kami telah menciptakan manusia dalam bentuk yang sebaik-baiknya,', 'laqad khalaqnal-insāna fī aḥsani taqwīm'),
(6102, 95, 5, 'ثُمَّ رَدَدْنٰهُ اَسْفَلَ سَافِلِيْنَۙ', 'kemudian Kami kembalikan dia ke tempat yang serendah-rendahnya,', 'ṡumma radadnāhu asfala sāfilīn'),
(6103, 95, 6, 'اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَلَهُمْ اَجْرٌ غَيْرُ مَمْنُوْنٍۗ', 'kecuali orang-orang yang beriman dan mengerjakan kebajikan; maka mereka akan mendapat pahala yang tidak ada putus-putusnya.', 'illallażīna āmanụ wa \'amiluṣ-ṣāliḥāti fa lahum ajrun gairu mamnụn'),
(6104, 95, 7, 'فَمَا يُكَذِّبُكَ بَعْدُ بِالدِّيْنِۗ', 'Maka apa yang menyebabkan (mereka) mendustakanmu (tentang) hari pembalasan setelah (adanya keterangan-keterangan) itu?', 'fa mā yukażżibuka ba\'du bid-dīn'),
(6105, 95, 8, 'اَلَيْسَ اللّٰهُ بِاَحْكَمِ الْحٰكِمِيْنَ', 'Bukankah Allah hakim yang paling adil?', 'a laisallāhu bi`aḥkamil-ḥākimīn'),
(6106, 96, 1, 'اِقْرَأْ بِاسْمِ رَبِّكَ الَّذِيْ خَلَقَۚ', 'Bacalah dengan (menyebut) nama Tuhanmu yang menciptakan,', 'iqra` bismi rabbikallażī khalaq'),
(6107, 96, 2, 'خَلَقَ الْاِنْسَانَ مِنْ عَلَقٍۚ', 'Dia telah menciptakan manusia dari segumpal darah.', 'khalaqal-insāna min \'alaq'),
(6108, 96, 3, 'اِقْرَأْ وَرَبُّكَ الْاَكْرَمُۙ', 'Bacalah, dan Tuhanmulah Yang Mahamulia,', 'iqra` wa rabbukal-akram'),
(6109, 96, 4, 'الَّذِيْ عَلَّمَ بِالْقَلَمِۙ', 'Yang mengajar (manusia) dengan pena.', 'allażī \'allama bil-qalam'),
(6110, 96, 5, 'عَلَّمَ الْاِنْسَانَ مَا لَمْ يَعْلَمْۗ', 'Dia mengajarkan manusia apa yang tidak diketahuinya.', '\'allamal-insāna mā lam ya\'lam'),
(6111, 96, 6, 'كَلَّآ اِنَّ الْاِنْسَانَ لَيَطْغٰىٓ ۙ', 'Sekali-kali tidak! Sungguh, manusia itu benar-benar melampaui batas,', 'kallā innal-insāna layaṭgā'),
(6112, 96, 7, 'اَنْ رَّاٰهُ اسْتَغْنٰىۗ', 'apabila melihat dirinya serba cukup.', 'ar ra`āhustagnā'),
(6113, 96, 8, 'اِنَّ اِلٰى رَبِّكَ الرُّجْعٰىۗ', 'Sungguh, hanya kepada Tuhanmulah tempat kembali(mu).', 'inna ilā rabbikar-ruj\'ā'),
(6114, 96, 9, 'اَرَاَيْتَ الَّذِيْ يَنْهٰىۙ', 'Bagaimana pendapatmu tentang orang yang melarang?', 'a ra`aitallażī yan-hā'),
(6115, 96, 10, 'عَبْدًا اِذَا صَلّٰىۗ', 'seorang hamba ketika dia melaksanakan salat,', '\'abdan iżā ṣallā'),
(6116, 96, 11, 'اَرَاَيْتَ اِنْ كَانَ عَلَى الْهُدٰىٓۙ', 'bagaimana pendapatmu jika dia (yang dilarang salat itu) berada di atas kebenaran (petunjuk),', 'a ra`aita ing kāna \'alal-hudā'),
(6117, 96, 12, 'اَوْ اَمَرَ بِالتَّقْوٰىۗ', 'atau dia menyuruh bertakwa (kepada Allah)?', 'au amara bit-taqwā'),
(6118, 96, 13, 'اَرَاَيْتَ اِنْ كَذَّبَ وَتَوَلّٰىۗ', 'Bagaimana pendapatmu jika dia (yang melarang) itu mendustakan dan berpaling?', 'a ra`aita ing każżaba wa tawallā'),
(6119, 96, 14, 'اَلَمْ يَعْلَمْ بِاَنَّ اللّٰهَ يَرٰىۗ', 'Tidakkah dia mengetahui bahwa sesungguhnya Allah melihat (segala perbuatannya)?', 'a lam ya\'lam bi`annallāha yarā'),
(6120, 96, 15, 'كَلَّا لَىِٕنْ لَّمْ يَنْتَهِ ەۙ لَنَسْفَعًاۢ بِالنَّاصِيَةِۙ', 'Sekali-kali tidak! Sungguh, jika dia tidak berhenti (berbuat demikian) niscaya Kami tarik ubun-ubunnya, (ke dalam neraka),', 'kallā la`il lam yantahi lanasfa\'am bin-nāṣiyah'),
(6121, 96, 16, 'نَاصِيَةٍ كَاذِبَةٍ خَاطِئَةٍۚ', 'yaitu) ubun-ubun orang yang mendustakan dan durhaka.', 'nāṣiyating kāżibatin khāṭi`ah'),
(6122, 96, 17, 'فَلْيَدْعُ نَادِيَهٗۙ', 'Maka biarlah dia memanggil golongannya (untuk menolongnya),', 'falyad\'u nādiyah'),
(6123, 96, 18, 'سَنَدْعُ الزَّبَانِيَةَۙ', 'Kelak Kami akan memanggil Malaikat Zabaniyah, (penyiksa orang-orang yang berdosa),', 'sanad\'uz-zabāniyah'),
(6124, 96, 19, 'كَلَّاۗ لَا تُطِعْهُ وَاسْجُدْ وَاقْتَرِبْ ۩', 'sekali-kali tidak! Janganlah kamu patuh kepadanya; dan sujudlah serta dekatkanlah (dirimu kepada Allah).', 'kallā, lā tuṭi\'hu wasjud waqtarib'),
(6125, 97, 1, 'اِنَّآ اَنْزَلْنٰهُ فِيْ لَيْلَةِ الْقَدْرِ', 'Sesungguhnya Kami telah menurunkannya (Al-Qur\'an) pada malam qadar.', 'innā anzalnāhu fī lailatil-qadr'),
(6126, 97, 2, 'وَمَآ اَدْرٰىكَ مَا لَيْلَةُ الْقَدْرِۗ', 'Dan tahukah kamu apakah malam kemuliaan itu?', 'wa mā adrāka mā lailatul-qadr'),
(6127, 97, 3, 'لَيْلَةُ الْقَدْرِ ەۙ خَيْرٌ مِّنْ اَلْفِ شَهْرٍۗ', 'Malam kemuliaan itu lebih baik daripada seribu bulan.', 'lailatul-qadri khairum min alfi syahr'),
(6128, 97, 4, 'تَنَزَّلُ الْمَلٰۤىِٕكَةُ وَالرُّوْحُ فِيْهَا بِاِذْنِ رَبِّهِمْۚ مِنْ كُلِّ اَمْرٍۛ', 'Pada malam itu turun para malaikat dan Ruh (Jibril) dengan izin Tuhannya untuk mengatur semua urusan.', 'tanazzalul-malā`ikatu war-rụḥu fīhā bi`iżni rabbihim, ming kulli amr'),
(6129, 97, 5, 'سَلٰمٌ ۛهِيَ حَتّٰى مَطْلَعِ الْفَجْرِ', 'Sejahteralah (malam itu) sampai terbit fajar.', 'salāmun hiya ḥattā maṭla\'il-fajr'),
(6130, 98, 1, 'لَمْ يَكُنِ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَالْمُشْرِكِيْنَ مُنْفَكِّيْنَ حَتّٰى تَأْتِيَهُمُ الْبَيِّنَةُۙ', 'Orang-orang yang kafir dari golongan Ahli Kitab dan orang-orang musyrik tidak akan meninggalkan (agama mereka) sampai datang kepada mereka bukti yang nyata,', 'lam yakunillażīna kafarụ min ahlil-kitābi wal-musyrikīna munfakkīna ḥattā ta`tiyahumul-bayyinah'),
(6131, 98, 2, 'رَسُوْلٌ مِّنَ اللّٰهِ يَتْلُوْا صُحُفًا مُّطَهَّرَةًۙ', '(yaitu) seorang Rasul dari Allah (Muhammad) yang membacakan lembaran-lembaran yang suci (Al-Qur\'an),', 'rasụlum minallāhi yatlụ ṣuḥufam muṭahharah'),
(6132, 98, 3, 'فِيْهَا كُتُبٌ قَيِّمَةٌ ۗ', 'di dalamnya terdapat (isi) kitab-kitab yang lurus (benar).', 'fīhā kutubung qayyimah'),
(6133, 98, 4, 'وَمَا تَفَرَّقَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنَةُ ۗ', 'Dan tidaklah terpecah-belah orang-orang Ahli Kitab melainkan setelah datang kepada mereka bukti yang nyata.', 'wa mā tafarraqallażīna ụtul-kitāba illā mim ba\'di mā jā`at-humul-bayyinah'),
(6134, 98, 5, 'وَمَآ اُمِرُوْٓا اِلَّا لِيَعْبُدُوا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۙ حُنَفَاۤءَ وَيُقِيْمُوا الصَّلٰوةَ وَيُؤْتُوا الزَّكٰوةَ وَذٰلِكَ دِيْنُ الْقَيِّمَةِۗ', 'Padahal mereka hanya diperintah menyembah Allah dengan ikhlas menaati-Nya semata-mata karena (menjalankan) agama, dan juga agar melaksanakan salat dan menunaikan zakat; dan yang demikian itulah agama yang lurus (benar).', 'wa mā umirū illā liya\'budullāha mukhliṣīna lahud-dīna ḥunafā`a wa yuqīmuṣ-ṣalāta wa yu`tuz-zakāta wa żālika dīnul-qayyimah'),
(6135, 98, 6, 'اِنَّ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَالْمُشْرِكِيْنَ فِيْ نَارِ جَهَنَّمَ خٰلِدِيْنَ فِيْهَاۗ اُولٰۤىِٕكَ هُمْ شَرُّ الْبَرِيَّةِۗ', 'Sungguh, orang-orang yang kafir dari golongan Ahli Kitab dan orang-orang musyrik (akan masuk) ke neraka Jahanam; mereka kekal di dalamnya selama-lamanya. Mereka itu adalah sejahat-jahat makhluk.', 'innallażīna kafarụ min ahlil-kitābi wal-musyrikīna fī nāri jahannama khālidīna fīhā, ulā`ika hum syarrul-bariyyah'),
(6136, 98, 7, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اُولٰۤىِٕكَ هُمْ خَيْرُ الْبَرِيَّةِۗ', 'Sungguh, orang-orang yang beriman dan mengerjakan kebajikan, mereka itu adalah sebaik-baik makhluk.', 'innallażīna āmanụ wa \'amiluṣ-ṣāliḥāti ulā`ika hum khairul-bariyyah'),
(6137, 98, 8, 'جَزَاۤؤُهُمْ عِنْدَ رَبِّهِمْ جَنّٰتُ عَدْنٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗرَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُ ۗ ذٰلِكَ لِمَنْ خَشِيَ رَبَّهٗ', 'Balasan mereka di sisi Tuhan mereka ialah surga ’Adn yang mengalir di bawahnya sungai-sungai; mereka kekal di dalamnya selama-lamanya. Allah rida terhadap mereka dan mereka pun rida kepada-Nya. Yang demikian itu adalah (balasan) bagi orang yang takut kepada Tuhannya.', 'jazā`uhum \'inda rabbihim jannātu \'adnin tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, raḍiyallāhu \'an-hum wa raḍụ \'an-h, żālika liman khasyiya rabbah'),
(6138, 99, 1, 'اِذَا زُلْزِلَتِ الْاَرْضُ زِلْزَالَهَاۙ', 'Apabila bumi diguncangkan dengan guncangan yang dahsyat,', 'iżā zulzilatil-arḍu zilzālahā'),
(6139, 99, 2, 'وَاَخْرَجَتِ الْاَرْضُ اَثْقَالَهَاۙ', 'dan bumi telah mengeluarkan beban-beban berat (yang dikandung)nya,', 'wa akhrajatil-arḍu aṡqālahā'),
(6140, 99, 3, 'وَقَالَ الْاِنْسَانُ مَا لَهَاۚ', 'Dan manusia bertanya, “Apa yang terjadi pada bumi ini?”', 'wa qālal-insānu mā lahā'),
(6141, 99, 4, 'يَوْمَىِٕذٍ تُحَدِّثُ اَخْبَارَهَاۙ', 'Pada hari itu bumi menyampaikan beritanya,', 'yauma`iżin tuḥaddiṡu akhbārahā'),
(6142, 99, 5, 'بِاَنَّ رَبَّكَ اَوْحٰى لَهَاۗ', 'karena sesungguhnya Tuhanmu telah memerintahkan (yang sedemikian itu) padanya.', 'bi`anna rabbaka auḥā lahā'),
(6143, 99, 6, 'يَوْمَىِٕذٍ يَّصْدُرُ النَّاسُ اَشْتَاتًا ەۙ لِّيُرَوْا اَعْمَالَهُمْۗ', 'Pada hari itu manusia keluar dari kuburnya dalam keadaan berkelompok-kelompok, untuk diperlihatkan kepada mereka (balasan) semua perbuatannya.', 'yauma`iżiy yaṣdurun-nāsu asytātal liyurau a\'mālahum'),
(6144, 99, 7, 'فَمَنْ يَّعْمَلْ مِثْقَالَ ذَرَّةٍ خَيْرًا يَّرَهٗۚ', 'Maka barangsiapa mengerjakan kebaikan seberat zarrah, niscaya dia akan melihat (balasan)nya,', 'fa may ya\'mal miṡqāla żarratin khairay yarah'),
(6145, 99, 8, 'وَمَنْ يَّعْمَلْ مِثْقَالَ ذَرَّةٍ شَرًّا يَّرَهٗ', 'dan barangsiapa mengerjakan kejahatan seberat zarrah, niscaya dia akan melihat (balasan)nya.', 'wa may ya\'mal miṡqāla żarratin syarray yarah'),
(6146, 100, 1, 'وَالْعٰدِيٰتِ ضَبْحًاۙ', 'Demi kuda perang yang berlari kencang terengah-engah,', 'wal-\'ādiyāti ḍab-ḥā'),
(6147, 100, 2, 'فَالْمُوْرِيٰتِ قَدْحًاۙ', 'dan kuda yang memercikkan bunga api (dengan pukulan kuku kakinya),', 'fal-mụriyāti qad-ḥā'),
(6148, 100, 3, 'فَالْمُغِيْرٰتِ صُبْحًاۙ', 'dan kuda yang menyerang (dengan tiba-tiba) pada waktu pagi,', 'fal-mugīrāti ṣub-ḥā'),
(6149, 100, 4, 'فَاَثَرْنَ بِهٖ نَقْعًاۙ', 'sehingga menerbangkan debu,', 'fa aṡarna bihī naq\'ā'),
(6150, 100, 5, 'فَوَسَطْنَ بِهٖ جَمْعًاۙ', 'lalu menyerbu ke tengah-tengah kumpulan musuh,', 'fa wasaṭna bihī jam\'ā'),
(6151, 100, 6, 'اِنَّ الْاِنْسَانَ لِرَبِّهٖ لَكَنُوْدٌ ۚ', 'sungguh, manusia itu sangat ingkar, (tidak bersyukur) kepada Tuhannya,', 'innal-insāna lirabbihī lakanụd'),
(6152, 100, 7, 'وَاِنَّهٗ عَلٰى ذٰلِكَ لَشَهِيْدٌۚ', 'dan sesungguhnya dia (manusia) menyaksikan (mengakui) keingkarannya,', 'wa innahụ \'alā żālika lasyahīd'),
(6153, 100, 8, 'وَاِنَّهٗ لِحُبِّ الْخَيْرِ لَشَدِيْدٌ ۗ', 'dan sesungguhnya cintanya kepada harta benar-benar berlebihan.', 'wa innahụ liḥubbil-khairi lasyadīd'),
(6154, 100, 9, '۞ اَفَلَا يَعْلَمُ اِذَا بُعْثِرَ مَا فِى الْقُبُوْرِۙ', 'Maka tidakkah dia mengetahui apabila apa yang di dalam kubur dikeluarkan,', 'a fa lā ya\'lamu iżā bu\'ṡira mā fil-qubụr'),
(6155, 100, 10, 'وَحُصِّلَ مَا فِى الصُّدُوْرِۙ', 'dan apa yang tersimpan di dalam dada dilahirkan?', 'wa huṣṣila mā fiṣ-ṣudụr'),
(6156, 100, 11, 'اِنَّ رَبَّهُمْ بِهِمْ يَوْمَىِٕذٍ لَّخَبِيْرٌ', 'sungguh, Tuhan mereka pada hari itu Mahateliti terhadap keadaan mereka.', 'inna rabbahum bihim yauma`iżil lakhabīr'),
(6157, 101, 1, 'اَلْقَارِعَةُۙ', 'Hari Kiamat,', 'al-qāri\'ah'),
(6158, 101, 2, 'مَا الْقَارِعَةُ ۚ', 'Apakah hari Kiamat itu?', 'mal-qāri\'ah'),
(6159, 101, 3, 'وَمَآ اَدْرٰىكَ مَا الْقَارِعَةُ ۗ', 'Dan tahukah kamu apakah hari Kiamat itu?', 'wa mā adrāka mal-qāri\'ah'),
(6160, 101, 4, 'يَوْمَ يَكُوْنُ النَّاسُ كَالْفَرَاشِ الْمَبْثُوْثِۙ', 'Pada hari itu manusia seperti laron yang beterbangan,', 'yauma yakụnun-nāsu kal-farāsyil-mabṡụṡ'),
(6161, 101, 5, 'وَتَكُوْنُ الْجِبَالُ كَالْعِهْنِ الْمَنْفُوْشِۗ', 'dan gunung-gunung seperti bulu yang dihambur-hamburkan.', 'wa takụnul-jibālu kal-\'ihnil-manfụsy'),
(6162, 101, 6, 'فَاَمَّا مَنْ ثَقُلَتْ مَوَازِينُهٗۙ', 'Maka adapun orang yang berat timbangan (kebaikan)nya,', 'fa ammā man ṡaqulat mawāzīnuh'),
(6163, 101, 7, 'فَهُوَ فِيْ عِيْشَةٍ رَّاضِيَةٍۗ', 'maka dia berada dalam kehidupan yang memuaskan (senang).', 'fa huwa fī \'īsyatir rāḍiyah'),
(6164, 101, 8, 'وَاَمَّا مَنْ خَفَّتْ مَوَازِيْنُهٗۙ', 'Dan adapun orang yang ringan timbangan (kebaikan)nya,', 'wa ammā man khaffat mawāzīnuh'),
(6165, 101, 9, 'فَاُمُّهُ هَاوِيَةٌ ۗ', 'maka tempat kembalinya adalah neraka Hawiyah.', 'fa ummuhụ hāwiyah'),
(6166, 101, 10, 'وَمَآ اَدْرٰىكَ مَا هِيَهْۗ', 'Dan tahukah kamu apakah neraka Hawiyah itu?', 'wa mā adrāka mā hiyah'),
(6167, 101, 11, 'نَارٌ حَامِيَةٌ', '(Yaitu) api yang sangat panas.', 'nārun ḥāmiyah'),
(6168, 102, 1, 'اَلْهٰىكُمُ التَّكَاثُرُۙ', 'Bermegah-megahan telah melalaikan kamu,', 'al-hākumut-takāṡur'),
(6169, 102, 2, 'حَتّٰى زُرْتُمُ الْمَقَابِرَۗ', 'sampai kamu masuk ke dalam kubur.', 'ḥattā zurtumul-maqābir'),
(6170, 102, 3, 'كَلَّا سَوْفَ تَعْلَمُوْنَۙ', 'Sekali-kali tidak! Kelak kamu akan mengetahui (akibat perbuatanmu itu),', 'kallā saufa ta\'lamụn'),
(6171, 102, 4, 'ثُمَّ كَلَّا سَوْفَ تَعْلَمُوْنَ', 'kemudian sekali-kali tidak! Kelak kamu akan mengetahui.', 'ṡumma kallā saufa ta\'lamụn'),
(6172, 102, 5, 'كَلَّا لَوْ تَعْلَمُوْنَ عِلْمَ الْيَقِيْنِۗ', 'Sekali-kali tidak! Sekiranya kamu mengetahui dengan pasti,', 'kallā lau ta\'lamụna \'ilmal-yaqīn'),
(6173, 102, 6, 'لَتَرَوُنَّ الْجَحِيْمَۙ', 'niscaya kamu benar-benar akan melihat neraka Jahim,', 'latarawunnal-jaḥīm'),
(6174, 102, 7, 'ثُمَّ لَتَرَوُنَّهَا عَيْنَ الْيَقِيْنِۙ', 'kemudian kamu benar-benar akan melihatnya dengan mata kepala sendiri,', 'ṡumma latarawunnahā \'ainal-yaqīn'),
(6175, 102, 8, 'ثُمَّ لَتُسْـَٔلُنَّ يَوْمَىِٕذٍ عَنِ النَّعِيْمِ', 'kemudian kamu benar-benar akan ditanya pada hari itu tentang kenikmatan (yang megah di dunia itu).', 'ṡumma latus`alunna yauma`iżin \'anin-na\'īm'),
(6176, 103, 1, 'وَالْعَصْرِۙ', 'Demi masa,', 'wal-\'aṣr'),
(6177, 103, 2, 'اِنَّ الْاِنْسَانَ لَفِيْ خُسْرٍۙ', 'sungguh, manusia berada dalam kerugian,', 'innal-insāna lafī khusr'),
(6178, 103, 3, 'اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَتَوَاصَوْا بِالْحَقِّ ەۙ وَتَوَاصَوْا بِالصَّبْرِ', 'kecuali orang-orang yang beriman dan mengerjakan kebajikan serta saling menasihati untuk kebenaran dan saling menasihati untuk kesabaran.', 'illallażīna āmanụ wa \'amiluṣ-ṣāliḥāti wa tawāṣau bil-ḥaqqi wa tawāṣau biṣ-ṣabr'),
(6179, 104, 1, 'وَيْلٌ لِّكُلِّ هُمَزَةٍ لُّمَزَةٍۙ', 'Celakalah bagi setiap pengumpat dan pencela,', 'wailul likulli humazatil lumazah'),
(6180, 104, 2, ' ۨالَّذِيْ جَمَعَ مَالًا وَّعَدَّدَهٗۙ', 'yang mengumpulkan harta dan menghitung-hitungnya,', 'allażī jama\'a mālaw wa \'addadah'),
(6181, 104, 3, 'يَحْسَبُ اَنَّ مَالَهٗٓ اَخْلَدَهٗۚ', 'dia (manusia) mengira bahwa hartanya itu dapat mengekalkannya.', 'yaḥsabu anna mālahū akhladah'),
(6182, 104, 4, 'كَلَّا لَيُنْۢبَذَنَّ فِى الْحُطَمَةِۖ', 'Sekali-kali tidak! Pasti dia akan dilemparkan ke dalam (neraka) Hutamah.', 'kallā layumbażanna fil-ḥuṭamah'),
(6183, 104, 5, 'وَمَآ اَدْرٰىكَ مَا الْحُطَمَةُ ۗ', 'Dan tahukah kamu apakah (neraka) Hutamah itu?', 'wa mā adrāka mal-ḥuṭamah'),
(6184, 104, 6, 'نَارُ اللّٰهِ الْمُوْقَدَةُۙ', '(Yaitu) api (azab) Allah yang dinyalakan,', 'nārullāhil-mụqadah'),
(6185, 104, 7, 'الَّتِيْ تَطَّلِعُ عَلَى الْاَفْـِٕدَةِۗ', 'yang (membakar) sampai ke hati.', 'allatī taṭṭali\'u \'alal-af`idah'),
(6186, 104, 8, 'اِنَّهَا عَلَيْهِمْ مُّؤْصَدَةٌۙ', 'Sungguh, api itu ditutup rapat atas (diri) mereka,', 'innahā \'alaihim mu`ṣadah'),
(6187, 104, 9, 'فِيْ عَمَدٍ مُّمَدَّدَةٍ', '(sedang mereka itu) diikat pada tiang-tiang yang panjang.', 'fī \'amadim mumaddadah'),
(6188, 105, 1, 'اَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِاَصْحٰبِ الْفِيْلِۗ', 'Tidakkah engkau (Muhammad) perhatikan bagaimana Tuhanmu telah bertindak terhadap pasukan bergajah?', 'a lam tara kaifa fa\'ala rabbuka bi`aṣ-ḥābil-fīl'),
(6189, 105, 2, 'اَلَمْ يَجْعَلْ كَيْدَهُمْ فِيْ تَضْلِيْلٍۙ', 'Bukankah Dia telah menjadikan tipu daya mereka itu sia-sia?', 'a lam yaj\'al kaidahum fī taḍlīl'),
(6190, 105, 3, 'وَّاَرْسَلَ عَلَيْهِمْ طَيْرًا اَبَابِيْلَۙ', 'dan Dia mengirimkan kepada mereka burung yang berbondong-bondong,', 'wa arsala \'alaihim ṭairan abābīl'),
(6191, 105, 4, 'تَرْمِيْهِمْ بِحِجَارَةٍ مِّنْ سِجِّيْلٍۙ', 'yang melempari mereka dengan batu dari tanah liat yang dibakar,', 'tarmīhim biḥijāratim min sijjīl'),
(6192, 105, 5, 'فَجَعَلَهُمْ كَعَصْفٍ مَّأْكُوْلٍ', 'sehingga mereka dijadikan-Nya seperti daun-daun yang dimakan (ulat).', 'fa ja\'alahum ka\'aṣfim ma`kụl'),
(6193, 106, 1, 'لِاِيْلٰفِ قُرَيْشٍۙ', 'Karena kebiasaan orang-orang Quraisy,', 'li`īlāfi quraīsy'),
(6194, 106, 2, 'اٖلٰفِهِمْ رِحْلَةَ الشِّتَاۤءِ وَالصَّيْفِۚ', '(yaitu) kebiasaan mereka bepergian pada musim dingin dan musim panas.', 'īlāfihim riḥlatasy-syitā`i waṣ-ṣaīf'),
(6195, 106, 3, 'فَلْيَعْبُدُوْا رَبَّ هٰذَا الْبَيْتِۙ', 'Maka hendaklah mereka menyembah Tuhan (pemilik) rumah ini (Ka‘bah),', 'falya\'budụ rabba hāżal-baīt'),
(6196, 106, 4, 'الَّذِيْٓ اَطْعَمَهُمْ مِّنْ جُوْعٍ ەۙ وَّاٰمَنَهُمْ مِّنْ خَوْفٍ', 'yang telah memberi makanan kepada mereka untuk menghilangkan lapar dan mengamankan mereka dari rasa ketakutan.', 'allażī aṭ\'amahum min jụ\'iw wa āmanahum min khaụf'),
(6197, 107, 1, 'اَرَءَيْتَ الَّذِيْ يُكَذِّبُ بِالدِّيْنِۗ', 'Tahukah kamu (orang) yang mendustakan agama?', 'a ra`aitallażī yukażżibu bid-dīn'),
(6198, 107, 2, 'فَذٰلِكَ الَّذِيْ يَدُعُّ الْيَتِيْمَۙ', 'Maka itulah orang yang menghardik anak yatim,', 'fa żālikallażī yadu\'\'ul-yatīm'),
(6199, 107, 3, 'وَلَا يَحُضُّ عَلٰى طَعَامِ الْمِسْكِيْنِۗ', 'dan tidak mendorong memberi makan orang miskin.', 'wa lā yaḥuḍḍu \'alā ṭa\'āmil-miskīn'),
(6200, 107, 4, 'فَوَيْلٌ لِّلْمُصَلِّيْنَۙ', 'Maka celakalah orang yang salat,', 'fa wailul lil-muṣallīn'),
(6201, 107, 5, 'الَّذِيْنَ هُمْ عَنْ صَلَاتِهِمْ سَاهُوْنَۙ', '(yaitu) orang-orang yang lalai terhadap salatnya,', 'allażīna hum \'an ṣalātihim sāhụn'),
(6202, 107, 6, 'الَّذِيْنَ هُمْ يُرَاۤءُوْنَۙ', 'yang berbuat ria,', 'allażīna hum yurā`ụn'),
(6203, 107, 7, 'وَيَمْنَعُوْنَ الْمَاعُوْنَ', 'dan enggan (memberikan) bantuan.', 'wa yamna\'ụnal-mā\'ụn'),
(6204, 108, 1, 'اِنَّآ اَعْطَيْنٰكَ الْكَوْثَرَۗ', 'Sungguh, Kami telah memberimu (Muhammad) nikmat yang banyak.', 'innā a\'ṭainākal-kauṡar'),
(6205, 108, 2, 'فَصَلِّ لِرَبِّكَ وَانْحَرْۗ', 'Maka laksanakanlah salat karena Tuhanmu, dan berkurbanlah (sebagai ibadah dan mendekatkan diri kepada Allah).', 'fa ṣalli lirabbika wan-ḥar'),
(6206, 108, 3, 'اِنَّ شَانِئَكَ هُوَ الْاَبْتَرُ', 'Sungguh, orang-orang yang membencimu dialah yang terputus (dari rahmat Allah).', 'inna syāni`aka huwal-abtar'),
(6207, 109, 1, 'قُلْ يٰٓاَيُّهَا الْكٰفِرُوْنَۙ', 'Katakanlah (Muhammad), “Wahai orang-orang kafir!', 'qul yā ayyuhal-kāfirụn'),
(6208, 109, 2, 'لَآ اَعْبُدُ مَا تَعْبُدُوْنَۙ', 'aku tidak akan menyembah apa yang kamu sembah,', 'lā a\'budu mā ta\'budụn'),
(6209, 109, 3, 'وَلَآ اَنْتُمْ عٰبِدُوْنَ مَآ اَعْبُدُۚ', 'dan kamu bukan penyembah apa yang aku sembah,', 'wa lā antum \'ābidụna mā a\'bud'),
(6210, 109, 4, 'وَلَآ اَنَا۠ عَابِدٌ مَّا عَبَدْتُّمْۙ', 'dan aku tidak pernah menjadi penyembah apa yang kamu sembah,', 'wa lā ana \'ābidum mā \'abattum'),
(6211, 109, 5, 'وَلَآ اَنْتُمْ عٰبِدُوْنَ مَآ اَعْبُدُۗ', 'dan kamu tidak pernah (pula) menjadi penyembah apa yang aku sembah.', 'wa lā antum \'ābidụna mā a\'bud'),
(6212, 109, 6, 'لَكُمْ دِيْنُكُمْ وَلِيَ دِيْنِ', 'Untukmu agamamu, dan untukku agamaku.”', 'lakum dīnukum wa liya dīn'),
(6213, 110, 1, 'اِذَا جَاۤءَ نَصْرُ اللّٰهِ وَالْفَتْحُۙ', 'Apabila telah datang pertolongan Allah dan kemenangan,', 'iżā jā`a naṣrullāhi wal-fat-ḥ'),
(6214, 110, 2, 'وَرَاَيْتَ النَّاسَ يَدْخُلُوْنَ فِيْ دِيْنِ اللّٰهِ اَفْوَاجًاۙ', 'dan engkau melihat manusia berbondong-bondong masuk agama Allah,', 'wa ra`aitan-nāsa yadkhulụna fī dīnillāhi afwājā'),
(6215, 110, 3, 'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَاسْتَغْفِرْهُۗ اِنَّهٗ كَانَ تَوَّابًا', 'maka bertasbihlah dalam dengan Tuhanmu dan mohonlah ampunan kepada-Nya. Sungguh, Dia Maha Penerima tobat.', 'fa sabbiḥ biḥamdi rabbika wastagfir-h, innahụ kāna tawwābā'),
(6216, 111, 1, 'تَبَّتْ يَدَآ اَبِيْ لَهَبٍ وَّتَبَّۗ', 'Binasalah kedua tangan Abu Lahab dan benar-benar binasa dia!', 'tabbat yadā abī lahabiw wa tabb'),
(6217, 111, 2, 'مَآ اَغْنٰى عَنْهُ مَالُهٗ وَمَا كَسَبَۗ', 'Tidaklah berguna baginya hartanya dan apa yang dia usahakan.', 'mā agnā \'an-hu māluhụ wa mā kasab'),
(6218, 111, 3, 'سَيَصْلٰى نَارًا ذَاتَ لَهَبٍۙ', 'Kelak dia akan masuk ke dalam api yang bergejolak (neraka).', 'sayaṣlā nāran żāta lahab'),
(6219, 111, 4, 'وَّامْرَاَتُهٗ ۗحَمَّالَةَ الْحَطَبِۚ', 'Dan (begitu pula) istrinya, pembawa kayu bakar (penyebar fitnah).', 'wamra`atuh, ḥammālatal-ḥaṭab'),
(6220, 111, 5, 'فِيْ جِيْدِهَا حَبْلٌ مِّنْ مَّسَدٍ', 'Di lehernya ada tali dari sabut yang dipintal.', 'fī jīdihā ḥablum mim masad'),
(6221, 112, 1, 'قُلْ هُوَ اللّٰهُ اَحَدٌۚ', 'Katakanlah (Muhammad), “Dialah Allah, Yang Maha Esa.', 'qul huwallāhu aḥad'),
(6222, 112, 2, 'اَللّٰهُ الصَّمَدُۚ', 'Allah tempat meminta segala sesuatu.', 'allāhuṣ-ṣamad'),
(6223, 112, 3, 'لَمْ يَلِدْ وَلَمْ يُوْلَدْۙ', '(Allah) tidak beranak dan tidak pula diperanakkan.', 'lam yalid wa lam yụlad'),
(6224, 112, 4, 'وَلَمْ يَكُنْ لَّهٗ كُفُوًا اَحَدٌ', 'Dan tidak ada sesuatu yang setara dengan Dia.”', 'wa lam yakul lahụ kufuwan aḥad'),
(6225, 113, 1, 'قُلْ اَعُوْذُ بِرَبِّ الْفَلَقِۙ', 'Katakanlah, “Aku berlindung kepada Tuhan yang menguasai subuh (fajar),', 'qul a\'ụżu birabbil-falaq'),
(6226, 113, 2, 'مِنْ شَرِّ مَا خَلَقَۙ', 'dari kejahatan (makhluk yang) Dia ciptakan,', 'min syarri mā khalaq'),
(6227, 113, 3, 'وَمِنْ شَرِّ غَاسِقٍ اِذَا وَقَبَۙ', 'dan dari kejahatan malam apabila telah gelap gulita,', 'wa min syarri gāsiqin iżā waqab'),
(6228, 113, 4, 'وَمِنْ شَرِّ النَّفّٰثٰتِ فِى الْعُقَدِۙ', 'dan dari kejahatan (perempuan-perempuan) penyihir yang meniup pada buhul-buhul (talinya),', 'wa min syarrin-naffāṡāti fil-\'uqad'),
(6229, 113, 5, 'وَمِنْ شَرِّ حَاسِدٍ اِذَا حَسَدَ', 'dan dari kejahatan orang yang dengki apabila dia dengki.”', 'wa min syarri ḥāsidin iżā ḥasad'),
(6230, 114, 1, 'قُلْ اَعُوْذُ بِرَبِّ النَّاسِۙ', 'Katakanlah, “Aku berlindung kepada Tuhannya manusia,', 'qul a\'ụżu birabbin-nās'),
(6231, 114, 2, 'مَلِكِ النَّاسِۙ', 'Raja manusia,', 'malikin-nās'),
(6232, 114, 3, 'اِلٰهِ النَّاسِۙ', 'sembahan manusia,', 'ilāhin-nās'),
(6233, 114, 4, 'مِنْ شَرِّ الْوَسْوَاسِ ەۙ الْخَنَّاسِۖ', 'dari kejahatan (bisikan) setan yang bersembunyi,', 'min syarril-waswāsil-khannās'),
(6234, 114, 5, 'الَّذِيْ يُوَسْوِسُ فِيْ صُدُوْرِ النَّاسِۙ', 'yang membisikkan (kejahatan) ke dalam dada manusia,', 'allażī yuwaswisu fī ṣudụrin-nās'),
(6235, 114, 6, 'مِنَ الْجِنَّةِ وَالنَّاسِ', 'dari (golongan) jin dan manusia.”', 'minal-jinnati wan-nās');
